package com.example.dating.data.model.repository

import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await
import javax.inject.Inject
import com.example.dating.data.model.User
import javax.inject.Singleton

@Singleton
class HomeRepository @Inject constructor(
    private val db: FirebaseFirestore
) {
    suspend fun fetchProfiles(): List<String> {
        val currentUserId = FirebaseAuth.getInstance().currentUser?.uid
        val snapshot = db.collection("users").get().await()
        return snapshot.documents.mapNotNull { doc ->
            val uid = doc.id
            if (uid != currentUserId) uid else null
        }
    }

    suspend fun getUserProfilesByIds(userIds: List<String>): List<User> {
        if (userIds.isEmpty()) return emptyList()
        val snapshot = db.collection("users")
            .whereIn(com.google.firebase.firestore.FieldPath.documentId(), userIds.take(9))
            .get().await()
        return snapshot.documents.mapNotNull { doc ->
            val data = doc.data ?: return@mapNotNull null
            User(
                uid = doc.id,
                firstName = data["firstName"] as? String ?: "",
                lastName = data["lastName"] as? String ?: "",
                birthday = data["birthday"] as? String,
                imageUrl = (data["imageUrl"] as? List<String>) ?: emptyList(),
                avatarUrl = data["avatarUrl"] as? String,
                gender = data["gender"] as? String,
                job = data["job"] as? String,
                location = data["location"] as? String,
                description = data["description"] as? String,
                interests = (data["interests"] as? List<String>) ?: emptyList(),
                distance = (data["distance"] as? Long)?.toInt()
            )
        }
    }
}

package com.example.dating.data.model.repository
import com.google.firebase.auth.FirebaseAuth import com.google.firebase.firestore.FirebaseFirestore import kotlinx.coroutines.tasks.await import javax.inject.Inject import com.example.dating.data.model.User import javax.inject.Singleton
@Singleton class HomeRepository @Inject constructor( private val db: FirebaseFirestore ) { suspend fun fetchProfiles(): List<String> { val currentUserId = FirebaseAuth.getInstance().currentUser?.uid val snapshot = db.collection("users").get().await() val uids = snapshot.documents.mapNotNull { doc -> val uid = doc.id if (uid != currentUserId) uid else null } android.util.Log.d("HomeRepository", "Fetched UIDs: $uids") return uids }
suspend fun getUserProfilesByIds(userIds: List<String>): List<User> { if (userIds.isEmpty()) return emptyList() val snapshot = db.collection("users") .whereIn(com.google.firebase.firestore.FieldPath.documentId(), userIds.take(9)) .get().await() return snapshot.documents.mapNotNull { doc -> val data = doc.data ?: return@mapNotNull null User( uid = doc.id, firstName = data["firstName"] as? String ?: "", lastName = data["lastName"] as? String ?: "", birthday = data["birthday"] as? String, imageUrl = (data["imageUrl"] as? List<String>) ?: emptyList(), avatarUrl = data["avatarUrl"] as? String, gender = data["gender"] as? String, job = data["job"] as? String, location = data["location"] as? String, description = data["description"] as? String, interests = (data["interests"] as? List<String>) ?: emptyList(), distance = (data["distance"] as? Long)?.toInt() ) } } }

package com.example.dating.data.model.repository

import com.google.firebase.Timestamp
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class MatchRepository @Inject constructor() {
    private val db = FirebaseFirestore.getInstance()

    suspend fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        // Ensure userId1 < userId2 alphabetically
        val (firstId, secondId) = if (userId1 < userId2) userId1 to userId2 else userId2 to userId1
        val matchData = hashMapOf(
            "userId1" to firstId,
            "userId2" to secondId,
            "timestamp" to Timestamp.now(),
            "status" to status
        )
        // Check for duplicate (userId1/userId2 only)
        val query = db.collection("match")
            .whereEqualTo("userId1", firstId)
            .whereEqualTo("userId2", secondId)
            .get().await()
        android.util.Log.d("MatchRepository", "MatchQuery: userId1=$firstId, userId2=$secondId, resultCount=${query.size()}")
        if (query.isEmpty) {
            db.collection("match").add(matchData)
        }
    }

    suspend fun getUserDocument(userId: String): Map<String, Any?> {
        val doc = db.collection("users").document(userId).get().await()
        return doc.data ?: emptyMap()
    }

}

package com.example.dating.data.model.repository

import com.google.firebase.Timestamp
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await
import javax.inject.Inject
import javax.inject.Singleton

@Singleton
class MatchRepository @Inject constructor() {
    private val db = FirebaseFirestore.getInstance()

    suspend fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        // Ensure userId1 < userId2 alphabetically
        val (firstId, secondId) = if (userId1 < userId2) userId1 to userId2 else userId2 to userId1
        val matchData = hashMapOf(
            "userId1" to firstId,
            "userId2" to secondId,
            "timestamp" to Timestamp.now(),
            "status" to status
        )
        // Check for duplicate (userId1/userId2 only)
        val query = db.collection("match")
            .whereEqualTo("userId1", firstId)
            .whereEqualTo("userId2", secondId)
            .get().await()
        android.util.Log.d("MatchRepository", "MatchQuery: userId1=$firstId, userId2=$secondId, resultCount=${query.size()}")
        if (query.isEmpty) {
            db.collection("match").add(matchData)
        }
    }

    suspend fun getUserDocument(userId: String): Map<String, Any?> {
        val doc = db.collection("users").document(userId).get().await()
        return doc.data ?: emptyMap()
    }

}

package com.example.dating.data.model.repository

import com.example.dating.data.model.Resource
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await
import javax.inject.Inject
import com.example.dating.data.model.User

class UserRepositoryImpl @Inject constructor(
    private val firestore: FirebaseFirestore
) : UserRepository {

    fun getUser(uid: String): Flow<User?> = callbackFlow {
        val docRef = firestore.collection("users").document(uid)
        val listener = docRef.addSnapshotListener { snapshot, _ ->
            offer(snapshot?.toObject(User::class.java))
        }
        awaitClose { listener.remove() }
    }

    override suspend fun updateUser(user: User) {
        firestore.collection("users").document(user.uid).set(user).await()
    }

    override suspend fun saveProfile(
        userId: String,
        firstName: String,
        lastName: String,
        birthday: String?,
        imageUrl: String?
    ): Resource<Unit> {
        return try {
            val userMap = hashMapOf(
                "firstName" to firstName,
                "lastName" to lastName,
                "birthday" to birthday,
                "imageUrl" to imageUrl
            )
            firestore.collection("users").document(userId)
                .set(userMap)
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    override suspend fun updateGender(userId: String, gender: String): Resource<Unit> {
        return try {
            firestore.collection("users").document(userId)
                .update("gender", gender)
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    override suspend fun updateInterests(userId: String, interests: List<String>): Resource<Unit> {
        return try {
            firestore.collection("users").document(userId)
                .update("interests", interests)
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    override suspend fun updateJobLocationDescription(
        userId: String,
        job: String?,
        location: String?,
        description: String?
    ): Resource<Unit> {
        return try {
            firestore.collection("users").document(userId)
                .update(mapOf(
                    "job" to job,
                    "location" to location,
                    "description" to description
                ))
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }
}

package com.example.dating.data.model.repository

import com.example.dating.data.model.Resource
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.callbackFlow
import kotlinx.coroutines.tasks.await
import javax.inject.Inject
import com.example.dating.data.model.User

class UserRepositoryImpl @Inject constructor(
    private val firestore: FirebaseFirestore
) : UserRepository {

    override fun getUser(uid: String): Flow<User?> = callbackFlow {
        val docRef = firestore.collection("users").document(uid)
        val listener = docRef.addSnapshotListener { snapshot, _ ->
            trySend(snapshot?.toObject(User::class.java))
        }
        awaitClose { listener.remove() }
    }

    override suspend fun updateUser(user: User) {
        firestore.collection("users").document(user.uid).set(user).await()
    }

    override suspend fun saveProfile(
        user: User
    ) {
        firestore.collection("users").document(user.uid).set(user).await()
    }

    override suspend fun updateGender(uid: String, gender: String) {
        firestore.collection("users").document(uid).update("gender", gender).await()
    }

    override suspend fun updateInterests(uid: String, interests: List<String>) {
        firestore.collection("users").document(uid).update("interests", interests).await()
    }

    override suspend fun updateJobLocationDescription(uid: String, job: String?, location: String?, description: String?) {
        firestore.collection("users").document(uid).update(
            mapOf(
                "job" to job,
                "location" to location,
                "description" to description
            )
        ).await()
    }

    override suspend fun uploadAvatar(uid: String, uri: android.net.Uri): String {
        // TODO: Implement Firebase Storage upload and return the URL
        return ""
    }
}

package com.example.dating.navigation

import LoginScreen
import androidx.compose.runtime.Composable
import androidx.compose.runtime.remember
import androidx.navigation.NavHostController
import androidx.navigation.compose.NavHost
import androidx.navigation.compose.composable
import com.example.dating.ui.onboarding.OnboardingScreen
import androidx.compose.material3.Text
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.dating.ui.auth.SignUpScreen
import com.example.dating.ui.auth.PhoneNumberScreen
import com.example.dating.ui.auth.VerifyCodeScreen
import com.example.dating.ui.auth.VerifyEmailScreen
import com.example.dating.ui.auth.EmailScreen
import com.example.dating.ui.mainscreens.FavoriteScreen
import com.example.dating.ui.mainscreens.MatchScreen
import com.example.dating.ui.chat.MessagesScreen
import com.example.dating.ui.profile.GenderSelectionScreen
import com.example.dating.ui.profile.InterestSelectionScreen
import com.example.dating.ui.profile.EnableNotificationScreen
import com.example.dating.ui.profile.ProfileScreen
import com.example.dating.ui.profile.SearchFriendScreen
import com.example.dating.viewmodel.AuthViewModel
import com.example.dating.ui.mainscreens.HomeScreen
import com.example.dating.ui.profile.ProfileDetailsScreen
import com.example.dating.viewmodel.HomeViewModel
import com.example.dating.viewmodel.FavoriteViewModel
import androidx.hilt.navigation.compose.hiltViewModel
import androidx.navigation.compose.navigation

@Composable
fun AppNavGraph(navController: NavHostController, authViewModel: AuthViewModel = hiltViewModel()) {
    val authViewModel = hiltViewModel<AuthViewModel>()
    val messageViewModel = hiltViewModel<com.example.dating.viewmodel.MessagesViewModel>()

    NavHost(
        navController = navController,
        startDestination = "root_graph"
    ) {
        navigation(startDestination = Screen.Login.route, route = "root_graph") {
            // Onboarding
            composable(Screen.Onboarding.route) {
                OnboardingScreen(navController = navController)
            }

            composable(Screen.Register.route) {
                SignUpScreen(viewModel = authViewModel, navController = navController)
            }

            composable(Screen.PhoneNumber.route) {
                // Phone number screen can be implemented here
                PhoneNumberScreen(navController)
            }

            composable(Screen.Profile.route) {
                ProfileScreen(navController = navController)
            }

            composable(Screen.ProfileDetails.route) {
                ProfileDetailsScreen(navController = navController)
            }

            composable(Screen.EmailScreen.route) {
                EmailScreen(navController = navController)
            }
            composable(Screen.VerifyEmail.route) {
                VerifyEmailScreen(navController = navController)
            }

            composable(Screen.VerifyCode.route) {
                VerifyCodeScreen(navController)
            }

            composable(Screen.GenderSelect.route) {
                GenderSelectionScreen(navController)
            }

            composable(Screen.InterestSelect.route) {
                // Interest selection screen can be implemented here
                InterestSelectionScreen(navController)
            }

            composable(Screen.SearchFriend.route) {
                SearchFriendScreen(navController)
            }
            composable(Screen.EnableNotification.route) {
                // Enable notification screen can be implemented here
                EnableNotificationScreen(navController)
            }
            // Login
            composable(Screen.Login.route) {
                LoginScreen(viewModel = authViewModel, navController = navController)
            }

            // Home
            composable(Screen.Home.route) { backStackEntry ->
                val parentEntry = navController.getBackStackEntry("root_graph")
                val homeViewModel: HomeViewModel = hiltViewModel(parentEntry)
                HomeScreen(navController, homeViewModel)
            }

            // Favorite
            composable(Screen.Favorite.route) { backStackEntry ->
                val parentEntry = remember(backStackEntry) {
                    navController.getBackStackEntry("root_graph")
                }
                // Lấy viewModel scoped theo parentEntry
                val favoriteViewModel: FavoriteViewModel = hiltViewModel(parentEntry)
                FavoriteScreen(navController, favoriteViewModel)
            }

            // Match
            composable(
                route = Screen.Match.route + "/{matchedUserId}",
            ) { backStackEntry ->
                val matchedUserId = backStackEntry.arguments?.getString("matchedUserId")
                if (matchedUserId != null) {
                    MatchScreen(navController, matchedUserId)
                } else {
                    Text("No matched user ID provided")
                }
            }

            composable(Screen.Messages.route) {
                MessagesScreen(viewModel = messageViewModel)
            }
        }
    }
}

