package com.example.dating.ui.components

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.CircleShape
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.ArrowForward
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.navigation.NavController

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun FilterScreen(navController: NavController) {
    // State variables
    var selectedInterest by remember { mutableStateOf("Girls") }
    var location by remember { mutableStateOf("Chicago, USA") }
    var distance by remember { mutableFloatStateOf(40f) }
    var ageRange by remember { mutableStateOf(20f..28f) }

    // Bottom sheet or dialog content
    Column(
        modifier = Modifier
            .fillMaxWidth()
            .wrapContentHeight()
            .clip(RoundedCornerShape(topStart = 24.dp, topEnd = 24.dp))
            .background(Color.White)
            .padding(16.dp),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        // Notch
        Box(
            modifier = Modifier
                .width(40.dp)
                .height(4.dp)
                .background(Color.Gray.copy(alpha = 0.5f), CircleShape)
                .align(Alignment.CenterHorizontally)
        )

        Spacer(modifier = Modifier.height(16.dp))

        // Title and Clear
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "Filters",
                fontSize = 20.sp,
                fontWeight = FontWeight.Bold,
                color = Color.Black
            )
            TextButton(onClick = { /* TODO: Clear filters */ }) {
                Text(
                    text = "Clear",
                    color = Color(0xFFFF69B4), // Pink
                    fontSize = 16.sp
                )
            }
        }

        Spacer(modifier = Modifier.height(24.dp))

        // Interested in
        Text(
            text = "Interested in",
            fontSize = 16.sp,
            color = Color.Gray,
            modifier = Modifier.align(Alignment.Start)
        )
        Spacer(modifier = Modifier.height(8.dp))
        SegmentedButtonRow(modifier = Modifier.fillMaxWidth()) {
            SegmentedButton(
                selected = selectedInterest == "Girls",
                onClick = { selectedInterest = "Girls" },
                shape = RoundedCornerShape(topStart = 8.dp, bottomStart = 8.dp)
            ) {
                Text("Girls")
            }
            SegmentedButton(
                selected = selectedInterest == "Boys",
                onClick = { selectedInterest = "Boys" },
                shape = RoundedCornerShape(0.dp)
            ) {
                Text("Boys")
            }
            SegmentedButton(
                selected = selectedInterest == "Both",
                onClick = { selectedInterest = "Both" },
                shape = RoundedCornerShape(topEnd = 8.dp, bottomEnd = 8.dp)
            ) {
                Text("Both")
            }
        }

        Spacer(modifier = Modifier.height(24.dp))

        // Location
        Text(
            text = "Location",
            fontSize = 16.sp,
            color = Color.Gray,
            modifier = Modifier.align(Alignment.Start)
        )
        Spacer(modifier = Modifier.height(8.dp))
        OutlinedTextField(
            value = location,
            onValueChange = { /* Read-only, tap to select */ },
            readOnly = true,
            modifier = Modifier.fillMaxWidth(),
            trailingIcon = {
                Icon(
                    imageVector = Icons.Default.ArrowForward,
                    contentDescription = "Select location",
                    tint = Color(0xFFFF0000) // Red
                )
            },
            singleLine = true,
            interactionSource = remember { MutableInteractionSource() }.also { interactionSource ->
                // TODO: Handle click to open location selector
            }
        )

        Spacer(modifier = Modifier.height(24.dp))

        // Distance
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            Text(
                text = "Distance",
                fontSize = 16.sp,
                color = Color.Gray
            )
            Text(
                text = "${distance.toInt()}km",
                fontSize = 16.sp,
                color = Color.Gray
            )
        }
        Slider(
            value = distance,
            onValueChange = { distance = it },
            valueRange = 0f..100f, // Assuming max 100km
            modifier = Modifier.fillMaxWidth(),
            colors = SliderDefaults.colors(
                thumbColor = Color(0xFFBB86FC), // Purple
                activeTrackColor = Color(0xFFBB86FC),
                inactiveTrackColor = Color.LightGray
            )
        )

        Spacer(modifier = Modifier.height(24.dp))

        // Age
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            Text(
                text = "Age",
                fontSize = 16.sp,
                color = Color.Gray
            )
            Text(
                text = "${ageRange.start.toInt()}-${ageRange.endInclusive.toInt()}",
                fontSize = 16.sp,
                color = Color.Gray
            )
        }
        RangeSlider(
            value = ageRange,
            onValueChange = { ageRange = it },
            valueRange = 18f..100f, // Assuming age range
            modifier = Modifier.fillMaxWidth(),
            colors = SliderDefaults.colors(
                thumbColor = Color(0xFFBB86FC), // Purple
                activeTrackColor = Color(0xFFBB86FC),
                inactiveTrackColor = Color.LightGray
            )
        )

        Spacer(modifier = Modifier.height(32.dp))

        // Continue button
        Button(
            onClick = { /* TODO: Apply filters and navigate */ },
            modifier = Modifier
                .fillMaxWidth()
                .height(48.dp),
            colors = ButtonDefaults.buttonColors(containerColor = Color(0xFFBB86FC).copy(alpha = 0.2f)), // Light purple
            shape = RoundedCornerShape(24.dp)
        ) {
            Text(
                text = "Continue",
                color = Color.Black,
                fontSize = 16.sp,
                fontWeight = FontWeight.Bold
            )
        }
    }
}

package com.example.dating.ui.components

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.CircleShape
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.ArrowForward
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.navigation.NavController

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun FilterDialog(
    show: Boolean,
    onDismiss: () -> Unit,
    onApply: (selectedInterest: String, location: String, distance: Float, ageRange: ClosedFloatingPointRange<Float>) -> Unit
) {
    if (!show) return
    var selectedInterest by remember { mutableStateOf("Girls") }
    var location by remember { mutableStateOf("Chicago, USA") }
    var distance by remember { mutableStateOf(40f) }
    var ageRange by remember { mutableStateOf(20f..28f) }

    AlertDialog(
        onDismissRequest = onDismiss,
        confirmButton = {
            Button(
                onClick = {
                    onApply(selectedInterest, location, distance, ageRange)
                    onDismiss()
                },
                modifier = Modifier.fillMaxWidth().height(48.dp),
                colors = ButtonDefaults.buttonColors(containerColor = Color(0xFFBB86FC).copy(alpha = 0.2f)),
                shape = RoundedCornerShape(24.dp)
            ) {
                Text("Continue", color = Color.Black, fontSize = 16.sp, fontWeight = FontWeight.Bold)
            }
        },
        dismissButton = {
            TextButton(onClick = onDismiss) {
                Text("Cancel", color = Color.Gray)
            }
        },
        title = {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text("Filters", fontSize = 20.sp, fontWeight = FontWeight.Bold, color = Color.Black)
                TextButton(onClick = {
                    selectedInterest = "Girls"
                    location = "Chicago, USA"
                    distance = 40f
                    ageRange = 20f..28f
                }) {
                    Text("Clear", color = Color(0xFFFF69B4), fontSize = 16.sp)
                }
            }
        },
        text = {
            Column(modifier = Modifier.fillMaxWidth(), horizontalAlignment = Alignment.CenterHorizontally) {
                // Interested in
                Text("Interested in", fontSize = 16.sp, color = Color.Gray, modifier = Modifier.align(Alignment.Start))
                Spacer(modifier = Modifier.height(8.dp))
                Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.Center) {
                    FilterSegmentButton("Girls", selectedInterest == "Girls") { selectedInterest = "Girls" }
                    FilterSegmentButton("Boys", selectedInterest == "Boys") { selectedInterest = "Boys" }
                    FilterSegmentButton("Both", selectedInterest == "Both") { selectedInterest = "Both" }
                }
                Spacer(modifier = Modifier.height(24.dp))
                // Location
                Text("Location", fontSize = 16.sp, color = Color.Gray, modifier = Modifier.align(Alignment.Start))
                Spacer(modifier = Modifier.height(8.dp))
                OutlinedTextField(
                    value = location,
                    onValueChange = { location = it },
                    readOnly = false,
                    modifier = Modifier.fillMaxWidth(),
                    trailingIcon = {
                        Icon(imageVector = Icons.Default.ArrowForward, contentDescription = "Select location", tint = Color(0xFFFF0000))
                    },
                    singleLine = true
                )
                Spacer(modifier = Modifier.height(24.dp))
                // Distance
                Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.SpaceBetween) {
                    Text("Distance", fontSize = 16.sp, color = Color.Gray)
                    Text("${distance.toInt()}km", fontSize = 16.sp, color = Color.Gray)
                }
                Slider(
                    value = distance,
                    onValueChange = { distance = it },
                    valueRange = 0f..100f,
                    modifier = Modifier.fillMaxWidth(),
                    colors = SliderDefaults.colors(
                        thumbColor = Color(0xFFBB86FC),
                        activeTrackColor = Color(0xFFBB86FC),
                        inactiveTrackColor = Color.LightGray
                    )
                )
                Spacer(modifier = Modifier.height(24.dp))
                // Age
                Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.SpaceBetween) {
                    Text("Age", fontSize = 16.sp, color = Color.Gray)
                    Text("${ageRange.start.toInt()}-${ageRange.endInclusive.toInt()}", fontSize = 16.sp, color = Color.Gray)
                }
                RangeSlider(
                    value = ageRange,
                    onValueChange = { ageRange = it },
                    valueRange = 18f..100f,
                    modifier = Modifier.fillMaxWidth(),
                    colors = SliderDefaults.colors(
                        thumbColor = Color(0xFFBB86FC),
                        activeTrackColor = Color(0xFFBB86FC),
                        inactiveTrackColor = Color.LightGray
                    )
                )
            }
        },
        shape = RoundedCornerShape(24.dp),
        containerColor = Color.White
    )
}

@Composable
private fun FilterSegmentButton(text: String, selected: Boolean, onClick: () -> Unit) {
    Button(
        onClick = onClick,
        colors = ButtonDefaults.buttonColors(
            containerColor = if (selected) Color(0xFFBB86FC) else Color.LightGray,
            contentColor = if (selected) Color.White else Color.Black
        ),
        modifier = Modifier
            .weight(1f)
            .height(36.dp)
            .padding(horizontal = 2.dp),
        shape = RoundedCornerShape(8.dp)
    ) {
        Text(text)
    }
}

package com.example.dating.ui.mainscreens

import androidx.compose.foundation.Image
import androidx.compose.foundation.background
import androidx.compose.foundation.clickable
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.rememberScrollState
import androidx.compose.foundation.shape.CircleShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.Close
import androidx.compose.material.icons.filled.Person
import androidx.compose.material.icons.filled.Settings
import androidx.compose.material.icons.filled.ViewModule
import androidx.compose.material.icons.filled.Chat
import androidx.compose.material.icons.filled.Favorite
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.lifecycle.viewmodel.compose.viewModel
import com.example.dating.viewmodel.FavoriteViewModel
import com.example.dating.ui.theme.AppColors
import androidx.navigation.NavController
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.foundation.verticalScroll
import com.example.dating.data.model.User
import com.example.dating.data.model.Resource

@Composable
fun FavoriteScreen(navController: NavController) {
    val favoriteViewModel: FavoriteViewModel = viewModel()
    val likedMeProfilesResource by favoriteViewModel.likedMeProfiles.collectAsState()
    val usersResource by favoriteViewModel.usersState.collectAsState()
    LaunchedEffect(likedMeProfilesResource) {
        if (likedMeProfilesResource is Resource.Success) {
            val uids = (likedMeProfilesResource as Resource.Success<List<String>>).result
            favoriteViewModel.getUserProfilesByIds(uids)
        }
    }

    Box(
        modifier = Modifier
            .fillMaxSize()
            .background(AppColors.MainBackground)
    ) {
        Column(
            modifier = Modifier
                .fillMaxSize()
                .verticalScroll(rememberScrollState())
        ) {
            FavoriteHeader(navController)
            when (usersResource) {
                is Resource.Loading -> {
                    Box(modifier = Modifier.fillMaxWidth().height(200.dp), contentAlignment = Alignment.Center) {
                        CircularProgressIndicator()
                    }
                }
                is Resource.Failure -> {
                    val error = (usersResource as Resource.Failure).exception?.message ?: "Unknown error"
                    Box(modifier = Modifier.fillMaxWidth().height(200.dp), contentAlignment = Alignment.Center) {
                        Text("Error: $error", color = Color.Red)
                    }
                }
                is Resource.Success -> {
                    val users = (usersResource as Resource.Success<List<User>>).result
                    if (users.isEmpty()) {
                        Box(modifier = Modifier.fillMaxWidth().height(200.dp), contentAlignment = Alignment.Center) {
                            Text("No users found", color = Color.Gray)
                        }
                    } else {
                        Text("This is a list of people who have liked you.", fontSize = 16.sp, modifier = Modifier.padding(start = 32.dp, top = 8.dp, bottom = 8.dp, end = 16.dp), color = Color.Black)
                        ProfileGrid(
                            profiles = users,
                            navController = navController,
                            showDelete = false,
                            moreAvailable = users.size >= 9,
                            onMoreClick = { /* TODO: Show all likedMeProfiles */ }
                        )
                    }
                }
            }
        }
        Box(
            modifier = Modifier
                .align(Alignment.BottomCenter)
                .fillMaxWidth()
        ) {
            FavoriteBottomNavigationBar(navController)
        }
    }

}

@Composable
fun FavoriteHeader(navController: NavController) {
    Row(
        modifier = Modifier
            .fillMaxWidth()
            .padding(start = 48.dp, end = 48.dp, top = 40.dp, bottom = 30.dp),
        horizontalArrangement = Arrangement.SpaceBetween,
        verticalAlignment = Alignment.CenterVertically
    ) {
        IconButton(onClick = { navController.navigate("profile_details") }) {
            Icon(
                imageVector = Icons.Default.Person,
                contentDescription = "Profile",
                tint = AppColors.Text_Pink
            )
        }

        Text(
            text = "Matches",
            color = Color.Black,
            fontWeight = FontWeight.Bold,
            fontSize = 30.sp
        )

        IconButton(onClick = { navController.navigate("profile_details") }) {
            Icon(
                imageVector = Icons.Default.Settings,
                contentDescription = "Profile",
                tint = AppColors.Text_Pink
            )
        }
    }
}

@Composable
fun ProfileGrid(
    profiles: List<User>,
    navController: NavController,
    showDelete: Boolean,
    moreAvailable: Boolean,
    onDelete: ((User) -> Unit)? = null,
    onMoreClick: () -> Unit
) {
    var showAll by remember { mutableStateOf(false) }
    val profilesToShow = if (showAll) profiles else profiles.take(6)
    val rowCount = if (showAll) (profilesToShow.size + 1) / 2 else 3
    Column(modifier = Modifier.fillMaxWidth().padding(16.dp)) {
        for (row in 0 until rowCount) {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.spacedBy(16.dp),
            ) {
                for (col in 0 until 2) {
                    val idx = row * 2 + col
                    val profile = profilesToShow.getOrNull(idx)
                    if (profile != null) {
                        Box(
                            modifier = Modifier
                                .padding(8.dp)
                                .weight(1f)
                                .height(220.dp)
                                .clip(RoundedCornerShape(24.dp))
                                .background(Color(0xFF23222B))
                                .clickable {
                                    navController.navigate("profile_display/${profile.uid}")
                                }
                        ) {
                            // Profile image placeholder
                            Image(
                                imageVector = Icons.Default.Person,
                                contentDescription = "Profile",
                                modifier = Modifier
                                    .size(80.dp)
                                    .align(Alignment.TopCenter)
                                    .clip(CircleShape)
                                    .background(Color.Gray)
                            )
                            // Name, Age, Description
                            val name = (profile.firstName + " " + profile.lastName).trim().ifEmpty { "Unknown" }
                            val birthday = profile.birthday
                            val age = birthday?.let {
                                try {
                                    val year = it.split("/").getOrNull(2)?.toInt() ?: throw Exception("Invalid date")
                                    val currentYear = java.util.Calendar.getInstance().get(java.util.Calendar.YEAR)
                                    (currentYear - year).toString()
                                } catch (e: Exception) { "?" }
                            } ?: "?"
                            Column(
                                modifier = Modifier
                                    .align(Alignment.BottomCenter)
                                    .fillMaxWidth()
                                    .background(Color.White.copy(alpha = 0.85f), RoundedCornerShape(bottomStart = 24.dp, bottomEnd = 24.dp))
                                    .padding(8.dp),
                                horizontalAlignment = Alignment.CenterHorizontally
                            ) {
                                Text("$name, $age", fontWeight = FontWeight.Bold, fontSize = 16.sp, color = Color.Black, maxLines = 1)
                            }
                            if (showDelete && onDelete != null) {
                                IconButton(onClick = { onDelete(profile) }, modifier = Modifier.align(Alignment.TopEnd)) {
                                    Icon(Icons.Default.Close, contentDescription = "Delete", tint = Color.Red)
                                }
                            }
                        }
                    } else {
                        Spacer(modifier = Modifier.weight(1f))
                    }
                }
            }
        }
        if (!showAll && moreAvailable) {
            Button(onClick = { showAll = true }, modifier = Modifier.align(Alignment.CenterHorizontally).padding(8.dp)) {
                Text("More")
            }
        }
        if (showAll && profiles.size > 6) {
            Button(onClick = { showAll = false }, modifier = Modifier.align(Alignment.CenterHorizontally).padding(8.dp)) {
                Text("Show Less")
            }
        }
    }
}

@Composable
fun FavoriteBottomNavigationBar(navController: NavController) {
    Box(
        modifier = Modifier
            .fillMaxWidth()
            .height(103.dp)
            .background(Color.White.copy(alpha = 1.0f)),
    ) {
        Row(
            modifier = Modifier
                .fillMaxWidth()
                .padding(horizontal = 32.dp, vertical = 20.dp),
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            BottomNavIcon(
                icon = Icons.Default.ViewModule,
                isActive = false
            ) { navController.popBackStack()  }
            BottomNavIcon(
                icon = Icons.Default.Favorite,
                isActive = true
            ) { /* Already on favorite */ }
            BottomNavIcon(
                icon = Icons.Default.Chat,
                isActive = false
            ) { /* TODO: Handle navigation */ }
            BottomNavIcon(
                icon = Icons.Default.Person,
                isActive = false
            ) { /* TODO: Handle navigation */ }
        }
    }
}

