        OutlinedTextField(
            value = firstName,
            onValueChange = { firstName = it },
            label = { Text("First name") },
            singleLine = true,
            modifier = Modifier.fillMaxWidth(),
            shape = RoundedCornerShape(12.dp),
            colors = OutlinedTextFieldDefaults.colors(
                focusedBorderColor = AppColors.Text_Pink,
                unfocusedBorderColor = Color.LightGray
            )
        )

        Spacer(modifier = Modifier.height(16.dp))

        // Last Name Field
        OutlinedTextField(
            value = lastName,
            onValueChange = { lastName = it },
            label = { Text("Last name") },
            singleLine = true,
            modifier = Modifier.fillMaxWidth(),
            shape = RoundedCornerShape(12.dp),
            colors = OutlinedTextFieldDefaults.colors(
                focusedBorderColor = AppColors.Text_Pink,
                unfocusedBorderColor = Color.LightGray
            )
        )

        Spacer(modifier = Modifier.height(16.dp))

        // Birthday Button
        Card(
            modifier = Modifier
                .fillMaxWidth()
                .clickable { showCalendar = true },
            shape = RoundedCornerShape(12.dp),
            colors = CardDefaults.cardColors(containerColor = Color(0xFFF8E8F5))
        ) {
            Row(
                modifier = Modifier
                    .fillMaxWidth()
                    .padding(16.dp),
                verticalAlignment = Alignment.CenterVertically
            ) {
                Icon(
                    painter = painterResource(id = R.drawable.ic_profile_calender),
                    contentDescription = "Calendar",
                    modifier = Modifier.size(24.dp)
                )

                Spacer(modifier = Modifier.width(12.dp))

                Text(
                    text = birthday?.let {
                        SimpleDateFormat("dd/MM/yyyy", Locale.getDefault()).format(it)
                    } ?: "Choose birthday date",
                    fontSize = 16.sp,
                    fontWeight = FontWeight.Medium,
                    color = AppColors.Main_Primary
                )
            }
        }

        Spacer(modifier = Modifier.weight(1f))

        // Confirm Button
        Button(
            onClick = {
                isSaving = true
                saveError = null
                val user = viewModel.user.value?.copy(
                    firstName = firstName,
                    lastName = lastName,
                    birthday = birthday?.let { SimpleDateFormat("dd/MM/yyyy", Locale.getDefault()).format(it) },
                ) ?: User(
                    uid = "", // Set the correct uid if available
                    firstName = firstName,
                    lastName = lastName,
                    birthday = birthday?.let { SimpleDateFormat("dd/MM/yyyy", Locale.getDefault()).format(it) }
                )
                viewModel.updateProfile(user)
                isSaving = false
                navController.navigate("gender_select")
            },
            enabled = firstName.isNotBlank() && lastName.isNotBlank() && birthday != null && !isSaving,
            modifier = Modifier
                .fillMaxWidth()
                .height(56.dp),
            shape = RoundedCornerShape(12.dp),
            colors = ButtonDefaults.buttonColors(
                containerColor = AppColors.Main_Secondary1,
                disabledContainerColor = Color.LightGray
            )
        ) {
            if (isSaving) {
                CircularProgressIndicator(modifier = Modifier.size(24.dp), color = AppColors.Main_Primary)
            } else {
                Text(
                    text = "Confirm",
                    color = AppColors.Main_Primary,
                    fontSize = 16.sp,
                    fontWeight = FontWeight.Medium
                )
            }
        }
        if (saveError != null) {
            Text(
                text = saveError ?: "",
                color = Color.Red,
                modifier = Modifier.padding(top = 8.dp)
            )
        }

        Spacer(modifier = Modifier.height(32.dp))
    }

    // Custom Calendar Dialog
    if (showCalendar) {
        CustomCalendarDialog(
            onDateSelected = { selectedDate ->
                birthday = selectedDate
                showCalendar = false
            },
            onDismiss = { showCalendar = false }
        )
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.FavoriteRepository
import com.example.dating.data.model.User
import com.example.dating.data.model.Resource
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

@HiltViewModel
class FavoriteViewModel @Inject constructor(
    private val favoriteRepository: FavoriteRepository
) : ViewModel() {
    private val db = FirebaseFirestore.getInstance()
    private val _likedMeProfiles = MutableStateFlow<Resource<List<String>>>(Resource.Loading)
    val likedMeProfiles: StateFlow<Resource<List<String>>> = _likedMeProfiles

    private val _usersState = MutableStateFlow<Resource<List<User>>>(Resource.Loading)
    val usersState: StateFlow<Resource<List<User>>> = _usersState

    init {
        fetchFavorites()
    }

    fun fetchFavorites() {
        val currentUserId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        android.util.Log.d("FavoriteViewModel", "likedMeIds: $currentUserId")

        viewModelScope.launch {
            try {
                _likedMeProfiles.value = Resource.Loading
                val likedMeSnapshot = favoriteRepository.getFavoritesByLikedId(currentUserId)
                val likedMeIds = likedMeSnapshot.mapNotNull { it["likerId"] as? String }
                android.util.Log.d("FavoriteViewModel", "likedMeIds: $likedMeIds")
                _likedMeProfiles.value = Resource.Success(likedMeIds)
            } catch (e: Exception) {
                _likedMeProfiles.value = Resource.Failure(e as? Exception ?: Exception(e.message))
            }
        }
    }

    fun deleteFavorite(likedId: String) {
        val likerId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        viewModelScope.launch {
            try {
                favoriteRepository.deleteFavorite(likerId, likedId)
                fetchFavorites() // Refresh after delete
            } catch (_: Exception) {}
        }
    }

    fun getUserProfilesByIds(uids: List<String>) {
        viewModelScope.launch {
            _usersState.value = Resource.Loading
            try {
                val users = favoriteRepository.getUserProfilesByIds(uids)
                _usersState.value = Resource.Success(users)
            } catch (e: Exception) {
                _usersState.value = Resource.Failure(e as? Exception ?: Exception(e.message))
            }
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.FavoriteRepository
import com.example.dating.data.model.User
import com.example.dating.data.model.Resource
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

@HiltViewModel
class FavoriteViewModel @Inject constructor(
    private val favoriteRepository: FavoriteRepository
) : ViewModel() {
    private val db = FirebaseFirestore.getInstance()

    private val _usersState = MutableStateFlow<Resource<List<User>>>(Resource.Loading)
    val usersState: StateFlow<Resource<List<User>>> = _usersState

    init {
        fetchFavorites()
    }

    fun fetchFavorites() {
        val currentUserId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        android.util.Log.d("FavoriteViewModel", "currentUserId: $currentUserId")

        viewModelScope.launch {
            try {
                _usersState.value = Resource.Loading

                // Get list of users who liked me
                val likedMeSnapshot = favoriteRepository.getFavoritesByLikedId(currentUserId)
                val likedMeIds = likedMeSnapshot.mapNotNull { it["likerId"] as? String }
                android.util.Log.d("FavoriteViewModel", "likedMeIds: $likedMeIds")

                // If empty, return immediately
                if (likedMeIds.isEmpty()) {
                    _usersState.value = Resource.Success(emptyList())
                    return@launch
                }

                // Fetch user profiles
                val users = getUserProfilesByIds(likedMeIds)
                _usersState.value = Resource.Success(users)
            } catch (e: Exception) {
                _usersState.value = Resource.Failure(e)
            }
        }
    }

    private suspend fun getUserProfilesByIds(uids: List<String>): List<User> {
        return try {
            val snapshot = db.collection("users")
                .whereIn("uid", uids)
                .get()
                .await()

            snapshot.documents.mapNotNull { it.toObject(User::class.java) }
        } catch (e: Exception) {
            android.util.Log.e("FavoriteViewModel", "Error fetching users: ", e)
            emptyList()
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.AuthRepository
import com.example.dating.data.model.repository.UserRepository
import com.example.dating.data.model.Resource

@HiltViewModel
class ProfileViewModel @Inject constructor(
    private val userRepository: UserRepository,
    private val auth: AuthRepository,
    ) : ViewModel() {
    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _updateState = MutableStateFlow<Resource<Unit>?>(null)
    val updateState: StateFlow<Resource<Unit>?> = _updateState

    init {
        val uid = auth.currentUser?.uid
        if (uid != null) {
            viewModelScope.launch {
                userRepository.getUser(uid).collect { fetchedUser ->
                    android.util.Log.d("ProfileViewModel", "Fetched user: $fetchedUser")
                    _user.value = fetchedUser
                }
            }
        } else {
            android.util.Log.w("ProfileViewModel", "No UID found in AuthRepository")
        }
    }

    fun loadUser(uid: String) {
        viewModelScope.launch {
            userRepository.getUser(uid).collect { _user.value = it }
        }
    }

    fun updateProfile(user: User) {
        android.util.Log.d("ProfileViewModel", "updateProfile called with: $user")
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
            }
        }
    }

    fun updateGender(gender: String) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(gender = gender)
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateGender(updatedUser.uid, gender)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateInterests(interests: List<String>) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(interests = interests)
        android.util.Log.w("ProfileViewModel", currentUser.toString())

        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateInterests(updatedUser.uid, interests)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateJobLocationDescription(job: String?, location: String?, description: String?) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(job = job, location = location, description = description)
        viewModelScope.launch {
            userRepository.updateJobLocationDescription(updatedUser.uid, job, location, description)
            _user.value = updatedUser
        }
    }

    fun createUser(user: User) {
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.createUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
            }
        }
    }

    fun uploadAvatar(uri: android.net.Uri) {
        val uid = auth.currentUser?.uid ?: return
        val storage = com.google.firebase.storage.FirebaseStorage.getInstance()
        val avatarRef = storage.reference.child("avatars/$uid.jpg")
        viewModelScope.launch {
            try {
                avatarRef.putFile(uri).addOnSuccessListener {
                    avatarRef.downloadUrl.addOnSuccessListener { downloadUrl ->
                        viewModelScope.launch {
                            userRepository.updateAvatarUrl(uid, downloadUrl.toString())
                            android.util.Log.d("ProfileViewModel", "Avatar updated: $downloadUrl")
                        }
                    }
                }.addOnFailureListener { e ->
                    android.util.Log.e("ProfileViewModel", "Failed to upload avatar: ${e.message}")
                }
            } catch (e: Exception) {
                android.util.Log.e("ProfileViewModel", "Exception in uploadAvatar: ${e.message}")
            }
        }
    }
}

package com.example.dating.viewmodel

