package com.example.dating.data

import com.google.firebase.Timestamp
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await

class MatchRepository {
    private val db = FirebaseFirestore.getInstance()

    suspend fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        // Ensure userId1 < userId2 alphabetically
        val (firstId, secondId) = if (userId1 < userId2) userId1 to userId2 else userId2 to userId1
        val matchData = hashMapOf(
            "userId1" to firstId,
            "userId2" to secondId,
            "timestamp" to Timestamp.now(),
            "status" to status
        )
        // Check for duplicate (userId1/userId2 only)
        val query = db.collection("match")
            .whereEqualTo("userId1", firstId)
            .whereEqualTo("userId2", secondId)
            .get().await()
        android.util.Log.d("MatchRepository", "MatchQuery: userId1=$firstId, userId2=$secondId, resultCount=${query.size()}")
        if (query.isEmpty) {
            db.collection("match").add(matchData)
        }
    }
}

package com.example.dating.data.model.repository

import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await

class FavoriteRepository {
    private val db = FirebaseFirestore.getInstance()

    suspend fun addFavorite(likerId: String, likedId: String): Boolean {
        val favorite = hashMapOf(
            "likerId" to likerId,
            "likedId" to likedId,
            "timestamp" to Timestamp.now()
        )
        val query = db.collection("favorites")
            .whereEqualTo("likerId", likerId)
            .whereEqualTo("likedId", likedId)
            .get().await()
        return if (query.isEmpty) {
            db.collection("favorites").add(favorite)
            true
        } else {
            false
        }
    }

    suspend fun isMatch(likerId: String, likedId: String): Boolean {
        val matchQuery = db.collection("favorites")
            .whereEqualTo("likerId", likedId)
            .whereEqualTo("likedId", likerId)
            .get().await()
        return !matchQuery.isEmpty
    }

    suspend fun deleteFavorite(likerId: String, likedId: String) {
        val query = db.collection("favorites")
            .whereEqualTo("likerId", likerId)
            .whereEqualTo("likedId", likedId)
            .get().await()
        for (doc in query.documents) {
            db.collection("favorites").document(doc.id).delete().await()
        }
    }

    suspend fun getFavoritesByLikedId(likedId: String): List<Map<String, Any>> {
        val query = db.collection("favorites")
            .whereEqualTo("likedId", likedId)
            .get().await()
        return query.documents.mapNotNull { it.data }
    }

package com.example.dating.data.model.repository

import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await

class FavoriteRepository {
    private val db = FirebaseFirestore.getInstance()

    suspend fun addFavorite(likerId: String, likedId: String): Boolean {
        val favorite = hashMapOf(
            "likerId" to likerId,
            "likedId" to likedId,
            "timestamp" to Timestamp.now()
        )
        val query = db.collection("favorites")
            .whereEqualTo("likerId", likerId)
            .whereEqualTo("likedId", likedId)
            .get().await()
        return if (query.isEmpty) {
            db.collection("favorites").add(favorite)
            true
        } else {
            false
        }
    }

    suspend fun isMatch(likerId: String, likedId: String): Boolean {
        val matchQuery = db.collection("favorites")
            .whereEqualTo("likerId", likedId)
            .whereEqualTo("likedId", likerId)
            .get().await()
        return !matchQuery.isEmpty
    }

    suspend fun deleteFavorite(likerId: String, likedId: String) {
        val query = db.collection("favorites")
            .whereEqualTo("likerId", likerId)
            .whereEqualTo("likedId", likedId)
            .get().await()
        for (doc in query.documents) {
            db.collection("favorites").document(doc.id).delete().await()
        }
    }

    suspend fun getFavoritesByLikedId(likedId: String): List<Map<String, Any>> {
        val query = db.collection("favorites")
            .whereEqualTo("likedId", likedId)
            .get().await()
        return query.documents.mapNotNull { it.data }
    }

    suspend fun getUserProfilesByIds(userIds: List<String>): List<Map<String, Any>> {
        if (userIds.isEmpty()) return emptyList()
        val snapshot = db.collection("users")
            .whereIn(com.google.firebase.firestore.FieldPath.documentId(), userIds.take(9))
            .get().await()
        return snapshot.documents.mapNotNull { it.data?.plus("uid" to it.id) }
    }
}

package com.example.dating.data.model.repository

import com.google.firebase.Timestamp
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await

class MatchRepository {
    private val db = FirebaseFirestore.getInstance()

    suspend fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        // Ensure userId1 < userId2 alphabetically
        val (firstId, secondId) = if (userId1 < userId2) userId1 to userId2 else userId2 to userId1
        val matchData = hashMapOf(
            "userId1" to firstId,
            "userId2" to secondId,
            "timestamp" to Timestamp.now(),
            "status" to status
        )
        // Check for duplicate (userId1/userId2 only)
        val query = db.collection("match")
            .whereEqualTo("userId1", firstId)
            .whereEqualTo("userId2", secondId)
            .get().await()
        android.util.Log.d("MatchRepository", "MatchQuery: userId1=$firstId, userId2=$secondId, resultCount=${query.size()}")
        if (query.isEmpty) {
            db.collection("match").add(matchData)
        }
    }

    suspend fun getUserFirstName(uid: String): String {
        val db = FirebaseFirestore.getInstance()
        return try {
            val doc = db.collection("users").document(uid).get().await()
            doc.getString("firstName") ?: "You"
        }
}

package com.example.dating.data.model.repository

import com.google.firebase.Timestamp
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await

class MatchRepository {
    private val db = FirebaseFirestore.getInstance()

    suspend fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        // Ensure userId1 < userId2 alphabetically
        val (firstId, secondId) = if (userId1 < userId2) userId1 to userId2 else userId2 to userId1
        val matchData = hashMapOf(
            "userId1" to firstId,
            "userId2" to secondId,
            "timestamp" to Timestamp.now(),
            "status" to status
        )
        // Check for duplicate (userId1/userId2 only)
        val query = db.collection("match")
            .whereEqualTo("userId1", firstId)
            .whereEqualTo("userId2", secondId)
            .get().await()
        android.util.Log.d("MatchRepository", "MatchQuery: userId1=$firstId, userId2=$secondId, resultCount=${query.size()}")
        if (query.isEmpty) {
            db.collection("match").add(matchData)
        }
    }

    suspend fun getUserFirstName(uid: String): String {
        val db = FirebaseFirestore.getInstance()
        return try {
            val doc = db.collection("users").document(uid).get().await()
            val firstName = doc.getString("firstName")
            android.util.Log.d("MatchRepository", "getUserFirstName: uid=$uid, firstName=$firstName, docExists=${doc.exists()}")
            firstName ?: "You"
        } catch (e: Exception) {
            android.util.Log.e("MatchRepository", "getUserFirstName error: uid=$uid, exception=${e.message}")
            "You"
        }
    }
}

package com.example.dating.ui.components

import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.ArrowForward
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.spandroidx.compose.foundation.layout
import
@Composable
fun FilterDialog(
    show: Boolean,
    onDismiss: () -> Unit,
    onApply: (selectedInterest: String, location: String, distance: Float, ageRange: ClosedFloatingPointRange<Float>) -> Unit
) {
    if (!show) return
    var selectedInterest by remember { mutableStateOf("Girls") }
    var location by remember { mutableStateOf("Chicago, USA") }
    var distance by remember { mutableStateOf(40f) }
    var ageRange by remember { mutableStateOf(20f..28f) }

    AlertDialog(
        onDismissRequest = onDismiss,
        confirmButton = {
            Button(
                onClick = {
                    onApply(selectedInterest, location, distance, ageRange)
                    onDismiss()
                },
                modifier = Modifier.fillMaxWidth().height(48.dp),
                colors = ButtonDefaults.buttonColors(containerColor = Color(0xFFBB86FC).copy(alpha = 0.2f)),
                shape = RoundedCornerShape(24.dp)
            ) {
                Text("Continue", color = Color.Black, fontSize = 16.sp, fontWeight = FontWeight.Bold)
            }
        },
        dismissButton = {
            TextButton(onClick = onDismiss) {
                Text("Cancel", color = Color.Gray)
            }
        },
        title = {
            Row(
                modifier = Modifier.fillMaxWidth(),
                horizontalArrangement = Arrangement.SpaceBetween,
                verticalAlignment = Alignment.CenterVertically
            ) {
                Text("Filters", fontSize = 20.sp, fontWeight = FontWeight.Bold, color = Color.Black)
                TextButton(onClick = {
                    selectedInterest = "Girls"
                    location = "Chicago, USA"
                    distance = 40f
                    ageRange = 20f..28f
                }) {
                    Text("Clear", color = Color(0xFFFF69B4), fontSize = 16.sp)
                }
            }
        },
        text = {
            Column(modifier = Modifier.fillMaxWidth(), horizontalAlignment = Alignment.CenterHorizontally) {
                // Interested in
                Text("Interested in", fontSize = 16.sp, color = Color.Gray, modifier = Modifier.align(Alignment.Start))
                Spacer(modifier = Modifier.height(8.dp))
                Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.Center) {
                    FilterSegmentButton("Girls", selectedInterest == "Girls") { selectedInterest = "Girls" }
                    FilterSegmentButton("Boys", selectedInterest == "Boys") { selectedInterest = "Boys" }
                    FilterSegmentButton("Both", selectedInterest == "Both") { selectedInterest = "Both" }
                }
                Spacer(modifier = Modifier.height(24.dp))
                // Location
                Text("Location", fontSize = 16.sp, color = Color.Gray, modifier = Modifier.align(Alignment.Start))
                Spacer(modifier = Modifier.height(8.dp))
                OutlinedTextField(
                    value = location,
                    onValueChange = { location = it },
                    readOnly = false,
                    modifier = Modifier.fillMaxWidth(),
                    trailingIcon = {
                        Icon(imageVector = Icons.Default.ArrowForward, contentDescription = "Select location", tint = Color(0xFFFF0000))
                    },
                    singleLine = true
                )
                Spacer(modifier = Modifier.height(24.dp))
                // Distance
                Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.SpaceBetween) {
                    Text("Distance", fontSize = 16.sp, color = Color.Gray)
                    Text("${distance.toInt()}km", fontSize = 16.sp, color = Color.Gray)
                }
                Slider(
                    value = distance,
                    onValueChange = { distance = it },
                    valueRange = 0f..100f,
                    modifier = Modifier.fillMaxWidth(),
                    colors = SliderDefaults.colors(
                        thumbColor = Color(0xFFBB86FC),
                        activeTrackColor = Color(0xFFBB86FC),
                        inactiveTrackColor = Color.LightGray
                    )
                )
                Spacer(modifier = Modifier.height(24.dp))
                // Age
                Row(modifier = Modifier.fillMaxWidth(), horizontalArrangement = Arrangement.SpaceBetween) {
                    Text("Age", fontSize = 16.sp, color = Color.Gray)
                    Text("${ageRange.start.toInt()}-${ageRange.endInclusive.toInt()}", fontSize = 16.sp, color = Color.Gray)
                }
                RangeSlider(
                    value = ageRange,
                    onValueChange = { ageRange = it },
                    valueRange = 18f..100f,
                    modifier = Modifier.fillMaxWidth(),
                    colors = SliderDefaults.colors(
                        thumbColor = Color(0xFFBB86FC),
                        activeTrackColor = Color(0xFFBB86FC),
                        inactiveTrackColor = Color.LightGray
                    )
                )
            }
        },
        shape = RoundedCornerShape(24.dp),
        containerColor = Color.White
    )
}

@Composable
private fun FilterSegmentButton(text: String, selected: Boolean, onClick: () -> Unit) {
    Button(
        onClick = onClick,
        colors = ButtonDefaults.buttonColors(
            containerColor = if (selected) Color(0xFFBB86FC) else Color.LightGray,
            contentColor = if (selected) Color.White else Color.Black
        ),
        modifier = Modifier
            .height(36.dp)
            .weight(1f)
            .padding(horizontal = 2.dp),
        shape = RoundedCornerShape(8.dp)
    ) {
        Text(text, maxLines = 1)
    }
}

