            BottomNavIcon(
                icon = Icons.Default.Favorite,
                isActive = false
            ) { navController.navigate("favorite") }
            BottomNavIcon(
                icon = Icons.Default.Chat,
                isActive = false
            ) { /* TODO: Handle navigation */ }
            BottomNavIcon(
                icon = Icons.Default.Person,
                isActive = false
            ) { /* TODO: Handle navigation */ }
        }
    }
}

package com.example.dating.ui.mainscreens

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch

class HomeViewModel : ViewModel() {
    private val db = FirebaseFirestore.getInstance()
    private val _addFavoriteState = MutableStateFlow<Result<Unit>?>(null)
    val addFavoriteState: StateFlow<Result<Unit>?> = _addFavoriteState

    fun addFavorite(likedId: String) {
        val likerId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        val favorite = hashMapOf(
            "likerId" to likerId,
            "likedId" to likedId,
            "timestamp" to Timestamp.now()
        )
        viewModelScope.launch {
            try {
                db.collection("favorites").add(favorite)
                _addFavoriteState.value = Result.success(Unit)
            } catch (e: Exception) {
                _addFavoriteState.value = Result.failure(e)
            }
        }
    }

    fun clearAddFavoriteState() {
        _addFavoriteState.value = null
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.FavoriteRepository
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FieldPath
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

class FavoriteViewModel(private val favoriteRepository: FavoriteRepository = FavoriteRepository()) : ViewModel() {
    private val db = FirebaseFirestore.getInstance()
    private val _likedMeProfiles = MutableStateFlow<List<Map<String, Any>>>(emptyList())
    val likedMeProfiles: StateFlow<List<Map<String, Any>>> = _likedMeProfiles
    private val _isLoading = MutableStateFlow(true)
    val isLoading: StateFlow<Boolean> = _isLoading
    private val _errorMessage = MutableStateFlow<String?>(null)
    val errorMessage: StateFlow<String?> = _errorMessage

    fun fetchFavorites() {
        val currentUserId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        viewModelScope.launch {
            try {
                _isLoading.value = true
                // Users who liked me
                val likedMeSnapshot = favoriteRepository.getFavoritesByLikedId(currentUserId)
                val likedMeIds = likedMeSnapshot.mapNotNull { it["likerId"] as? String }
                android.util.Log.d("FavoriteViewModel", "likedMeIds: $likedMeIds")
                val likedMeProfilesList = if (likedMeIds.isNotEmpty()) {
                    val likedMeProfilesSnapshot = db.collection("users")
                        .whereIn(FieldPath.documentId(), likedMeIds.take(9))
                        .get().await()
                    android.util.Log.d("FavoriteViewModel", "likedMeProfilesSnapshot: ${likedMeProfilesSnapshot.documents}")
                    likedMeProfilesSnapshot.documents.mapNotNull { it.data?.plus("uid" to it.id) }
                } else {
                    emptyList()
                }
                    val likedMeProfilesSnapshot = db.collection("users")
                        .whereIn(FieldPath.documentId(), likedMeIds.take(9))
                        .get().await()
                    android.util.Log.d("FavoriteViewModel", "likedMeProfilesSnapshot: ${likedMeProfilesSnapshot.documents}")
                    likedMeProfilesSnapshot.documents.mapNotNull { it.data?.plus("uid" to it.id) }
                } else {
                    emptyList()
                }
                _likedMeProfiles.value = likedMeProfilesList

                _isLoading.value = false
                _errorMessage.value = null
            } catch (e: Exception) {
                _errorMessage.value = e.message
                _isLoading.value = false
            }
        }
    }

    fun deleteFavorite(likedId: String) {
        val likerId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        viewModelScope.launch {
            try {
                favoriteRepository.deleteFavorite(likerId, likedId)
                fetchFavorites() // Refresh after delete
            } catch (_: Exception) {}
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.FavoriteRepository
import com.google.firebase.auth.FirebaseAuth
