import androidx.compose.material3.*
import androidx.compose.runtime.Composable
import androidx.compose.runtime.LaunchedEffect
import androidx.compose.runtime.collectAsState
import androidx.compose.runtime.getValue
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.draw.rotate
import androidx.compose.ui.graphics.Color
import androidx.compose.ui.text.font.FontWeight
import androidx.compose.ui.unit.dp
import androidx.compose.ui.unit.sp
import androidx.navigation.NavController
import com.example.dating.ui.theme.AppColors
import androidx.hilt.navigation.compose.hiltViewModel
import com.example.dating.viewmodel.MatchViewModel
import com.example.dating.viewmodel.ProfileViewModel

@Composable
fun MatchScreen(navController: NavController, matchedUserId: String?) {
    val matchViewModel: MatchViewModel = hiltViewModel()
    val profileViewModel: ProfileViewModel = hiltViewModel()

    val currentUser by profileViewModel.user.collectAsState()
    val currentUserInfo by matchViewModel.userInfo.collectAsState()
    val matchedUserInfo by matchViewModel.matchedUserInfo.collectAsState()

    // Set current user info in MatchViewModel
    LaunchedEffect(currentUser) {
        matchViewModel.setUser(currentUser)
    }

    // Set matched user info in MatchViewModel
    LaunchedEffect(matchedUserId) {
        if (matchedUserId != null) {
            val matchedUser = matchViewModel.getUserById(matchedUserId)
            matchViewModel.setMatchedUser(matchedUser)
        }
    }

    // Save the match when both users are available
    LaunchedEffect(currentUserInfo.value, matchedUserInfo.value) {
        if (currentUserInfo.value != null && matchedUserInfo.value != null) {
            matchViewModel.saveMatch(currentUserInfo.value.uid, matchedUserInfo.value.uid, true)
        }
    }

    Column(modifier = Modifier.fillMaxSize()) {


        // Main content
        Box(
            modifier = Modifier
                .fillMaxSize()
                .background(AppColors.GradientBackground) // Light purple
        ) {
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(horizontal = 32.dp),
                horizontalAlignment = Alignment.CenterHorizontally,
                verticalArrangement = Arrangement.Center
            ) {
                Box(modifier = Modifier.height(300.dp)) {

                    // Upper right profile photo (current user)
                    Box(
                        modifier = Modifier
                            .size(width = 190.dp, height = 280.dp)
                            .align(Alignment.TopEnd)
                            .offset(x = 55.dp, y = (-30).dp)
                            .rotate(10f)
                            .clip(RoundedCornerShape(24.dp))
                            .background(Color.Black)
                    ) {
                        Icon(
                            imageVector = Icons.Default.Person,
                            contentDescription = "Current User",
                            tint = Color.Gray.copy(alpha = 0.4f),
                            modifier = Modifier.fillMaxSize()
                        )
                    }

                    // Lower left profile photo (matched user)
                    Box(
                        modifier = Modifier
                            .size(width = 190.dp, height = 280.dp)
                            .align(Alignment.BottomStart)
                            .offset(x = (-55).dp, y = 90.dp)
                            .rotate(-10f)
                            .clip(RoundedCornerShape(24.dp))
                            .background(Color.Black)
                    ) {
                        Icon(
                            imageVector = Icons.Default.Person,
                            contentDescription = "Matched User",
                            tint = Color.Gray.copy(alpha = 0.4f),
                            modifier = Modifier.fillMaxSize()
                        )
                    }

                    // Heart for upper right photo
                    Box(
                        modifier = Modifier
                            .size(60.dp)
                            .align(Alignment.TopEnd)
                            .offset(x = (-75).dp, y = (-50).dp)
                            .rotate(10f)
                            .background(AppColors.Text_Pink, CircleShape),
                        contentAlignment = Alignment.Center
                    ) {
                        Icon(
                            imageVector = Icons.Default.Favorite,
                            contentDescription = "Heart",
                            tint = Color.White,
                            modifier = Modifier.size(22.dp)
                        )
                    }

                    // Heart for lower left photo
                    Box(
                        modifier = Modifier
                            .size(60.dp)
                            .align(Alignment.BottomStart)
                            .offset(x = (-60).dp, y = 120.dp)
                            .rotate(-10f)
                            .background(AppColors.Text_Pink, CircleShape),
                        contentAlignment = Alignment.Center
                    ) {
                        Icon(
                            imageVector = Icons.Default.Favorite,
                            contentDescription = "Heart",
                            tint = Color.White,
                            modifier = Modifier.size(22.dp)
                        )
                    }

                }
                Spacer(modifier = Modifier.height(160.dp))
                Text(
                    text = "It's a match, ${currentUserInfo?.firstName ?: "You"}!",
                    fontWeight = FontWeight.Bold,
                    fontSize = 32.sp,
                    color = Color.Black,
                    modifier = Modifier.padding(bottom = 8.dp)
                )
                // Example: Show matched user name
                Text(
                    text = "Matched with: ${matchedUserInfo?.firstName ?: "Unknown"}",
                    fontSize = 20.sp,
                    color = Color.Black,
                    modifier = Modifier.padding(bottom = 8.dp)
                )
                // You can use currentUserInfo?.avatarUrl and matchedUserInfo?.avatarUrl for images
                Text(
                    text = "Start a conversation now with each other.",
                    fontSize = 16.sp,
                    color = Color.Black,
                    modifier = Modifier.padding(bottom = 60.dp)
                )
                Button(
                    onClick = { /* TODO: Implement chat navigation */ },
                    colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF4A154B)),
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp)
                ) {
                    Text("Say hello", color = Color.White, fontSize = 18.sp, fontWeight = FontWeight.Bold)
                }
                Spacer(modifier = Modifier.height(16.dp))
                OutlinedButton(
                    onClick = { navController.popBackStack("home", inclusive = false) },
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp),
                    colors = ButtonDefaults.outlinedButtonColors(containerColor = Color.White)
                ) {
                    Text("Keep swiping", color = Color.Black, fontSize = 18.sp, fontWeight = FontWeight.Bold)
                }
            }
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.FavoriteRepository
import com.example.dating.data.model.User
import com.example.dating.data.model.Resource
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

@HiltViewModel
class FavoriteViewModel @Inject constructor(
    private val favoriteRepository: FavoriteRepository
) : ViewModel() {
    private val db = FirebaseFirestore.getInstance()
    private val _likedMeProfiles = MutableStateFlow<Resource<List<String>>>(Resource.Loading)
    val likedMeProfiles: StateFlow<Resource<List<String>>> = _likedMeProfiles

    private val _usersState = MutableStateFlow<Resource<List<User>>>(Resource.Loading)
    val usersState: StateFlow<Resource<List<User>>> = _usersState

    init {
        fetchFavorites()
    }

    fun fetchFavorites() {
        val currentUserId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        android.util.Log.d("FavoriteViewModel", "likedMeIds: $currentUserId")

        viewModelScope.launch {
            try {
                _likedMeProfiles.value = Resource.Loading
                val likedMeSnapshot = favoriteRepository.getFavoritesByLikedId(currentUserId)
                val likedMeIds = likedMeSnapshot.mapNotNull { it["likerId"] as? String }
                android.util.Log.d("FavoriteViewModel", "likedMeIds: $likedMeIds")
                _likedMeProfiles.value = Resource.Success(likedMeIds)
            } catch (e: Exception) {
                _likedMeProfiles.value = Resource.Failure(e as? Exception ?: Exception(e.message))
            }
        }
    }

    fun deleteFavorite(likedId: String) {
        val likerId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        viewModelScope.launch {
            try {
                favoriteRepository.deleteFavorite(likerId, likedId)
                fetchFavorites() // Refresh after delete
            } catch (_: Exception) {}
        }
    }

    fun getUserProfilesByIds(uids: List<String>) {
        viewModelScope.launch {
            _usersState.value = Resource.Loading
            try {
                val users = favoriteRepository.getUserProfilesByIds(uids)
                _usersState.value = Resource.Success(users)
            } catch (e: Exception) {
                _usersState.value = Resource.Failure(e as? Exception ?: Exception(e.message))
            }
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.FavoriteRepository
import com.example.dating.data.model.User
import com.example.dating.data.model.Resource
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await

@HiltViewModel
class FavoriteViewModel @Inject constructor(
    private val favoriteRepository: FavoriteRepository
) : ViewModel() {
    private val db = FirebaseFirestore.getInstance()

    private val _usersState = MutableStateFlow<Resource<List<User>>>(Resource.Loading)
    val usersState: StateFlow<Resource<List<User>>> = _usersState

    init {
        fetchFavorites()
    }

    fun fetchFavorites() {
        val currentUserId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        android.util.Log.d("FavoriteViewModel", "currentUserId: $currentUserId")

        viewModelScope.launch {
            try {
                _usersState.value = Resource.Loading

                // Get list of users who liked me
                val likedMeSnapshot = favoriteRepository.getFavoritesByLikedId(currentUserId)
                val likedMeIds = likedMeSnapshot.mapNotNull { it["likerId"] as? String }
                android.util.Log.d("FavoriteViewModel", "likedMeIds: $likedMeIds")

                // If empty, return immediately
                if (likedMeIds.isEmpty()) {
                    _usersState.value = Resource.Success(emptyList())
                    return@launch
                }

                // Fetch user profiles
                val users = getUserProfilesByIds(likedMeIds)
                _usersState.value = Resource.Success(users)
            } catch (e: Exception) {
                _usersState.value = Resource.Failure(e)
            }
        }
    }

    private suspend fun getUserProfilesByIds(uids: List<String>): List<User> {
        return try {
            val snapshot = db.collection("users")
                .whereIn("uid", uids)
                .get()
                .await()

            snapshot.documents.mapNotNull { it.toObject(User::class.java) }
        } catch (e: Exception) {
            android.util.Log.e("FavoriteViewModel", "Error fetching users: ", e)
            emptyList()
        }
    }
}
