package com.example.dating.data

import com.google.firebase.Timestamp
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await

class MatchRepository {
    private val db = FirebaseFirestore.getInstance()

    suspend fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        // Ensure userId1 < userId2 alphabetically
        val (firstId, secondId) = if (userId1 < userId2) userId1 to userId2 else userId2 to userId1
        val matchData = hashMapOf(
            "userId1" to firstId,
            "userId2" to secondId,
            "timestamp" to Timestamp.now(),
            "status" to status
        )
        // Check for duplicate (userId1/userId2 only)
        val query = db.collection("match")
            .whereEqualTo("userId1", firstId)
            .whereEqualTo("userId2", secondId)
            .get().await()
        android.util.Log.d("MatchRepository", "MatchQuery: userId1=$firstId, userId2=$secondId, resultCount=${query.size()}")
        if (query.isEmpty) {
            db.collection("match").add(matchData)
        }
    }
}

package com.example.dating.data.model.repository

import com.example.dating.data.model.ConversationPreview
import com.example.dating.data.model.User
import com.google.android.gms.tasks.Tasks
import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import com.google.firebase.firestore.Query
import kotlinx.coroutines.channels.awaitClose
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.callbackFlow
import kotlinx.coroutines.tasks.await
import javax.inject.Inject

class FirebaseMessagesRepository @Inject constructor(
    private val db: FirebaseFirestore,
    private val auth: FirebaseAuth
) {
    fun getConversations(): Flow<List<ConversationPreview>> = callbackFlow {
        val currentUid = auth.currentUser?.uid
        if(currentUid == null) {
            trySend(emptyList())
            awaitClose()
            return@callbackFlow
        }
        val query = db.collection("conversations")
            .whereArrayContains("participants", currentUid)
            .orderBy("lastTimestamp", Query.Direction.DESCENDING)

        val listener = query.addSnapshotListener { snapshot, _ ->
            if (snapshot == null) {
                trySend(emptyList())
                return@addSnapshotListener
            }

            val tasks = snapshot.documents.map { doc ->
                val data = doc.data ?: return@map null
                val cid = doc.id
                val participants = data["participants"] as List<String>
                val peerUid = participants.first { it != currentUid }

                val userTask = db.collection("users").document(peerUid).get()

                userTask.continueWith { userSnap ->
                    val user = userSnap.result?.toObject(User::class.java) ?: User(uid = peerUid)
                    val lastMessage = data["lastMessage"] as? String ?: ""
                    val timestamp = (data["lastTimestamp"] as? Timestamp)?.toDate()?.time ?: 0L
                    val unread = (data["unread"] as? Map<*, *>)?.get(currentUid) as? Long ?: 0L
                    val typing = (data["typing"] as? Map<*, *>)?.get(peerUid) as? Boolean ?: false

                    ConversationPreview(
                        id = cid,
                        peer = user,
                        lastMessage = lastMessage,
                        timeAgo = formatTimeAgo(timestamp),
                        unreadCount = unread.toInt(),
                        isTyping = typing
                    )
                }
            }

            Tasks.whenAllSuccess<ConversationPreview>(tasks)
                .addOnSuccessListener { result -> trySend(result) }
        }

        awaitClose { listener.remove() }
    }

    private fun formatTimeAgo(ms: Long): String {
        val min = ((System.currentTimeMillis() - ms) / 60000)
        return when {
            min < 1 -> "Just now"
            min < 60 -> "$min min"
            else -> "${min / 60} hour"
        }
    }
}

package com.example.dating.data.model.repository

import com.example.dating.data.model.ConversationPreview
import com.example.dating.data.model.User
import com.google.android.gms.tasks.Tasks
import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import com.google.firebase.firestore.Query
import kotlinx.coroutines.channels.awaitClose
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.callbackFlow
import kotlinx.coroutines.tasks.await
import javax.inject.Inject

class FirebaseMessagesRepository @Inject constructor(
    private val db: FirebaseFirestore,
    private val auth: FirebaseAuth
) {
    fun getConversations(): Flow<List<ConversationPreview>> = callbackFlow {
        val currentUid = auth.currentUser?.uid
        if(currentUid == null) {
            trySend(emptyList())
            awaitClose()
            return@callbackFlow
        }
        val query = db.collection("conversations")
            .whereArrayContains("participants", currentUid)
            .orderBy("lastTimestamp", Query.Direction.DESCENDING)

        val listener = query.addSnapshotListener { snapshot, _ ->
            if (snapshot == null) {
                trySend(emptyList())
                return@addSnapshotListener
            }

            val tasks = snapshot.documents.map { doc ->
                val data = doc.data ?: return@map null
                val cid = doc.id
                val participants = data["participants"] as List<String>
                val peerUid = participants.first { it != currentUid }

                val userTask = db.collection("users").document(peerUid).get()

                userTask.continueWith { userSnap ->
                    val user = userSnap.result?.toObject(User::class.java) ?: User(uid = peerUid)
                    val lastMessage = data["lastMessage"] as? String ?: ""
                    val timestamp = (data["lastTimestamp"] as? Timestamp)?.toDate()?.time ?: 0L
                    val unread = (data["unread"] as? Map<*, *>)?.get(currentUid) as? Long ?: 0L
                    val typing = (data["typing"] as? Map<*, *>)?.get(peerUid) as? Boolean ?: false

                    ConversationPreview(
                        id = cid,
                        peer = user,
                        lastMessage = lastMessage,
                        timeAgo = formatTimeAgo(timestamp),
                        unreadCount = unread.toInt(),
                        isTyping = typing
                    )
                }
            }

            Tasks.whenAllSuccess<ConversationPreview>(tasks)
                .addOnSuccessListener { result -> trySend(result) }
        }

        awaitClose { listener.remove() }
    }

    private fun formatTimeAgo(ms: Long): String {
        val min = ((System.currentTimeMillis() - ms) / 60000)
        return when {
            min < 1 -> "Just now"
            min < 60 -> "$min min"
            else -> "${min / 60} hour"
        }
    }

    suspend fun createConversation(userId1: String, userId2: String) {
        val conversationData = hashMapOf(
            "participants" to listOf(userId1, userId2),
            "lastMessage" to "",
            "lastTimestamp" to System.currentTimeMillis(),
            "unread" to mapOf(userId1 to 0, userId2 to 0)
        )
        db.collection("conversations").add(conversationData).await()
    }
}

package com.example.dating.data.model.repository

import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.Flow
import kotlinx.coroutines.flow.callbackFlow
import kotlinx.coroutines.channels.awaitClose
import kotlinx.coroutines.tasks.await
import javax.inject.Inject
import com.example.dating.data.model.User
import com.example.dating.data.model.Resource


class UserRepository @Inject constructor(
    private val firestore: FirebaseFirestore
)  {

    fun getUser(uid: String): Flow<User?> = callbackFlow {
        val docRef = firestore.collection("users").document(uid)
        android.util.Log.d("UserRepository", "getUser: docRef=$docRef " +
                "for uid=$uid")
        val listener = docRef.addSnapshotListener { snapshot, _ ->
            val user = snapshot?.toObject(User::class.java)
            // Ensure uid and imageUrl are set from document id and default if missing
            val userWithUid = user?.copy(
                uid = snapshot.id,
                imageUrl = user.imageUrl ?: emptyList()
            )
            android.util.Log.d("UserRepository", "getUser: uid=$uid, user=$userWithUid")
            trySend(userWithUid)
        }
        awaitClose { listener.remove() }
    }

    suspend fun updateUser(user: User): Resource<Unit> {
        return try {
            android.util.Log.d("ProfileViewModel", "updateProfile called with: $user")
            firestore.collection("users").document(user.uid).set(user).await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    suspend fun updateGender(uid: String, gender: String): Resource<Unit> {
        return try {
            firestore.collection("users").document(uid).update("gender", gender).await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    suspend fun updateInterests(uid: String, interests: List<String>): Resource<Unit> {
        return try {
            firestore.collection("users").document(uid).update("interests", interests).await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    suspend fun updateJobLocationDescription(uid: String, job: String?, location: String?, description: String?): Resource<Unit> {
        return try {
            firestore.collection("users").document(uid).update(
                mapOf(
                    "job" to job,
                    "location" to location,
                    "description" to description
                )
            ).await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    suspend fun uploadAvatar(uid: String, uri: android.net.Uri): String {
        // TODO: Implement Firebase Storage upload and return the URL
        return ""
    }

    suspend fun createUser(user: User): Resource<Unit> {
        return try {
            firestore.collection("users").document(user.uid).set(user).await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }
}

