@Composable
fun ProfileCard(
    profiles: List<User>,
    profileIndex: MutableState<Int>,
    handleProfileAction: suspend (Boolean, MutableState<Int>, List<User>, HomeViewModel) -> Unit,
    animateSwipe: suspend (Animatable<Float, *>, Float) -> Unit,
    homeViewModel: HomeViewModel = viewModel(),
    navController: NavController
) {
    val currentProfile = profiles.getOrNull(profileIndex.value)
    val nextProfile = profiles.getOrNull(profileIndex.value + 1)
    if (currentProfile == null && nextProfile == null) {
        Box(
            modifier = Modifier
                .fillMaxWidth()
                .height(900.dp),
            contentAlignment = Alignment.Center
        ) {
            Text("No profiles available", color = Color.Gray)
        }
        return
    }

    val scope = rememberCoroutineScope()
    val offsetX = remember { Animatable(0f) }
    val offsetY = remember { Animatable(0f) }
    val isDragging = remember { mutableStateOf(false) }
    val likeProgress = (offsetX.value / 200f).coerceIn(0f, 1f)
    val dislikeProgress = (-offsetX.value / 200f).coerceIn(0f, 1f)
    val iconAlpha = maxOf(likeProgress, dislikeProgress)
    val iconScale = 1f + 0.3f * iconAlpha
    val cardRotation = (offsetX.value / 15).coerceIn(-25f, 25f)
    val threshold = 200f

    Box(
        modifier = Modifier
            .fillMaxWidth()
            .height(550.dp)
            .padding(horizontal = 30.dp),
        contentAlignment = Alignment.Center
    ) {
        // Render next card first (always present if available)
        if (nextProfile != null) {
            ProfileCardContent(
                profile = nextProfile,
                modifier = Modifier
                    .fillMaxWidth()
                    .height(550.dp)
                    .clip(RoundedCornerShape(32.dp))
                    .background(Color(0xFF23222B))
                    .graphicsLayer(
                        scaleX = lerp(0.95f, 1f, iconAlpha),
                        scaleY = lerp(0.95f, 1f, iconAlpha),
                        alpha = lerp(0.7f, 1f, iconAlpha)
                    ),
                navController = navController
            )
        }
        // Top card (draggable, always present if available)
        if (currentProfile != null) {
            Box(
                modifier = Modifier
                    .fillMaxWidth()
                    .height(550.dp)
                    .offset { IntOffset(offsetX.value.toInt(), offsetY.value.toInt()) }
                    .rotate(cardRotation)
                    .clip(RoundedCornerShape(32.dp))
                    .background(Color(0xFF23222B))
                    .pointerInput(Unit) {
                        detectTapGestures(
                            onDoubleTap = {
                                currentProfile.uid?.let { uid ->
                                    navController.navigate(Screen.UserProfile.route(uid))
                                }
                            }
                        )
                    }
                    .pointerInput(profileIndex.value) {
                        detectDragGestures(
                            onDragStart = { isDragging.value = true },
                            onDragEnd = {
                                isDragging.value = false
                                scope.launch {
                                    when {
                                        offsetX.value > threshold -> {
                                            handleProfileAction(true, profileIndex, profiles, homeViewModel)
                                            animateSwipe(offsetX, 1f)
                                            offsetY.snapTo(0f)
                                        }
                                        offsetX.value < -threshold -> {
                                            handleProfileAction(false, profileIndex, profiles, homeViewModel)
                                            animateSwipe(offsetX, -1f)
                                            offsetY.snapTo(0f)
                                        }
                                        else -> {
                                            offsetX.animateTo(0f, spring(stiffness = Spring.StiffnessMedium))
                                            offsetY.animateTo(0f, spring(stiffness = Spring.StiffnessMedium))
                                        }
                                    }
                                }
                            },
                            onDrag = { change, dragAmount ->
                                scope.launch {
                                    offsetX.snapTo(offsetX.value + dragAmount.x)
                                    offsetY.snapTo(offsetY.value + dragAmount.y)
                                }
                            }
                        )
                    },
                contentAlignment = Alignment.Center
            ) {
                ProfileCardContent(
                    profile = currentProfile,
                    modifier = Modifier.fillMaxSize(),
                    navController = navController,
                    likeProgress = likeProgress,
                    dislikeProgress = dislikeProgress,
                    iconAlpha = iconAlpha,
                    iconScale = iconScale
                )
            }
        }
    }
}

@Composable
private fun ProfileCardContent(
    profile: User?,
    modifier: Modifier = Modifier,
    navController: NavController,
    likeProgress: Float = 0f,
    dislikeProgress: Float = 0f,
    iconAlpha: Float = 0f,
    iconScale: Float = 1f
) {
    if (profile == null) return
    val firstName = profile.firstName ?: ""
    val lastName = profile.lastName ?: ""
    val name = (firstName + " " + lastName).trim().ifEmpty { "Unknown" }
    val birthday = profile.birthday
    val age = birthday?.let {
        try {
            val year = it.split("/").getOrNull(2)?.toInt() ?: throw Exception("Invalid date")
            val currentYear = Calendar.getInstance().get(Calendar.YEAR)
            (currentYear - year).toString()
        } catch (e: Exception) {
            "?"
        }
    } ?: "?"
    val description = profile.description ?: "No description"
    val distance = profile.distance?.toString() ?: "1 km"

    Box(modifier = modifier) {
        if (profile.avatarUrl != null && profile.avatarUrl.isNotBlank()) {
            Image(
                painter = rememberAsyncImagePainter(model = profile.avatarUrl),
                contentDescription = "Profile Image",
                contentScale = ContentScale.Crop,
                modifier = Modifier
                    .fillMaxSize()
                    .clip(RoundedCornerShape(32.dp))
            )
        } else {
            Image(
                imageVector = Icons.Default.Person,
                contentDescription = "Profile Image",
                contentScale = ContentScale.Crop,
                modifier = Modifier
                    .fillMaxSize()
                    .clip(RoundedCornerShape(32.dp))
            )
        }
        Box(
            modifier = Modifier
                .padding(16.dp)
                .align(Alignment.TopStart)
                .background(Color.White.copy(alpha = 0.85f), RoundedCornerShape(50))
                .padding(horizontal = 16.dp, vertical = 6.dp)
        ) {
            Text(distance, color = Color.Black, fontWeight = FontWeight.Bold, fontSize = 14.sp)
        }
        Box(
            modifier = Modifier
                .fillMaxWidth()
                .height(120.dp)
                .align(Alignment.BottomCenter)
                .background(
                    Brush.verticalGradient(
                        colors = listOf(Color.Transparent, Color.Black),
                        startY = 0f,
                        endY = 300f
                    ),
                    shape = RoundedCornerShape(bottomStart = 32.dp, bottomEnd = 32.dp)
                )
        )
        Column(
            modifier = Modifier
                .align(Alignment.BottomCenter)
                .padding(16.dp),
            verticalArrangement = Arrangement.Center,
            horizontalAlignment = Alignment.CenterHorizontally
        ) {
            Text(
                text = "$name, $age",
                color = Color.White,
                fontWeight = FontWeight.Bold,
                fontSize = 28.sp,
                maxLines = 1,
                modifier = Modifier.fillMaxWidth(),
                textAlign = TextAlign.Center
            )
            Text(
                text = description,
                color = Color(0xFFCCCCCC),
                fontSize = 16.sp,
                maxLines = 1,
                modifier = Modifier.fillMaxWidth(),
                textAlign = TextAlign.Center
            )
        }
        if (likeProgress > 0.05f) {
            Icon(
                imageVector = Icons.Default.Favorite,
                contentDescription = "Like",
                tint = Color.Red.copy(alpha = iconAlpha),
                modifier = Modifier
                    .size((96f * iconScale).dp)
                    .align(Alignment.Center)
            )
        } else if (dislikeProgress > 0.05f) {
            Icon(
                imageVector = Icons.Default.Close,
                contentDescription = "Dislike",
                tint = Color.White.copy(alpha = iconAlpha),
                modifier = Modifier
                    .size((96f * iconScale).dp)
                    .align(Alignment.Center)
            )
        }
    }
}

@Composable
fun ActionButtons(
    profiles: List<User>,
    profileIndex: MutableState<Int>,
    handleProfileAction: suspend (Boolean, MutableState<Int>, List<User>, HomeViewModel) -> Unit,
    animateSwipe: suspend (Animatable<Float, *>, Float) -> Unit,
    homeViewModel: HomeViewModel = viewModel()
) {
    val scope = rememberCoroutineScope()
    val offsetX = remember { Animatable(0f) }

    Row(
        modifier = Modifier
            .fillMaxWidth()
            .padding(vertical = 24.dp),
        horizontalArrangement = Arrangement.spacedBy(25.dp, Alignment.CenterHorizontally),
        verticalAlignment = Alignment.CenterVertically
    ) {
        // Dislike Button
        ActionButton(
            icon = Icons.Default.Close,
            background = Color.White,
            iconTint = Color.Black,
            size = 75.dp,
            shadow = 8.dp
        ) {
            scope.launch {
                handleProfileAction(false, profileIndex, profiles, homeViewModel)
                animateSwipe(offsetX, -1f) // Pass direction explicitly
            }
        }
        // Super Like Button
        ActionButton(
            icon = Icons.Default.Favorite,
            background = AppColors.Text_Pink,
            iconTint = Color.White,
            size = 95.dp,
            shadow = 12.dp
        ) {
            scope.launch {
                handleProfileAction(true, profileIndex, profiles, homeViewModel)
                animateSwipe(offsetX, 1f) // Pass direction explicitly
            }
        }
        // Like Button (calls a different method for clarity)
        ActionButton(
            icon = Icons.Default.Star,
            background = Color(0xFF4A154B),
            iconTint = Color.White,
            size = 75.dp,
            shadow = 8.dp
        ) {
            scope.launch {
                handleProfileAction(true, profileIndex, profiles, homeViewModel)
                animateSwipe(offsetX, 1f) // Pass direction explicitly
            }
        }
    }
}

@Composable
fun ActionButton(
    icon: ImageVector,
    background: Color,
    iconTint: Color,
    size: Dp,
    shadow: Dp,
    onClick: () -> Unit
) {
    Box(
        modifier = Modifier
            .size(size)
            .shadow(shadow, CircleShape)
            .clip(CircleShape)
            .background(background)
            .clickable { onClick() },
        contentAlignment = Alignment.Center
    ) {
        Icon(
            imageVector = icon,
            contentDescription = null,
            tint = iconTint,
            modifier = Modifier.size(size * 0.5f)
        )
    }
}

package com.example.dating.ui.mainscreens

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch

class HomeViewModel : ViewModel() {
    private val db = FirebaseFirestore.getInstance()
    private val _addFavoriteState = MutableStateFlow<Result<Unit>?>(null)
    val addFavoriteState: StateFlow<Result<Unit>?> = _addFavoriteState

    fun addFavorite(likedId: String) {
        val likerId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        val favorite = hashMapOf(
            "likerId" to likerId,
            "likedId" to likedId,
            "timestamp" to Timestamp.now()
        )
        viewModelScope.launch {
            try {
                db.collection("favorites").add(favorite)
                _addFavoriteState.value = Result.success(Unit)
            } catch (e: Exception) {
                _addFavoriteState.value = Result.failure(e)
            }
        }
    }

    fun clearAddFavoriteState() {
        _addFavoriteState.value = null
    }
}

