        // Try to parse year from yyyy-MM-dd or dd/MM/yyyy or MM/dd/yyyy
        val year = when {
            it.contains("-") -> it.split("-").firstOrNull()?.toIntOrNull() // yyyy-MM-dd
            it.contains("/") -> it.split("/").lastOrNull()?.toIntOrNull() // dd/MM/yyyy or MM/dd/yyyy
