package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.MessagesFilterState
import com.example.dating.data.model.ConversationPreview
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.FirebaseMessagesRepository
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.delay
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import javax.inject.Inject
import com.example.dating.data.model.filterMessages

data class MessagesUiState(
    val isLoading: Boolean = false,
    val error: String? = null,
    val messages: List<ConversationPreview> = emptyList()
)

@HiltViewModel
class MessagesViewModel @Inject constructor(
    private val db: FirebaseFirestore,
    private val auth: FirebaseAuth
) : ViewModel() {

    private val _uiState = MutableStateFlow(MessagesUiState())
    val uiState: StateFlow<MessagesUiState> = _uiState

    private val _filterState = MutableStateFlow(MessagesFilterState())
    val filterState: StateFlow<MessagesFilterState> = _filterState // Change to StateFlow

    private var originalMessages = listOf<ConversationPreview>()
    private var loadedConversations = 0
    private var totalConversations = 0

    init {
        loadMessages()
    }

    fun updateFilter(newState: MessagesFilterState) {
        _filterState.value = newState
    }

    fun clearFilter() {
        _filterState.value = MessagesFilterState()
        _uiState.value = _uiState.value.copy(messages = originalMessages)
    }

    fun applyFilter() {
        val filtered = filterMessages(originalMessages, filterState.value)
        _uiState.value = _uiState.value.copy(messages = filtered)
    }

    private fun loadMessages() {
        _uiState.value = MessagesUiState(isLoading = true)
        val uid = auth.currentUser?.uid ?: return

        db.collection("conversations")
            .whereArrayContains("participants", uid)
            .get()
            .addOnSuccessListener { snapshot ->
                if (snapshot.isEmpty) {
                    _uiState.value = _uiState.value.copy(
                        isLoading = false,
                        messages = emptyList()
                    )
                    return@addOnSuccessListener
                }

                totalConversations = snapshot.size()
                loadedConversations = 0
                val conversations = mutableListOf<ConversationPreview>()

                snapshot.documents.forEach { doc ->
                    val participants = doc.get("participants") as? List<String> ?: return@forEach
                    val peerId = participants.find { it != uid } ?: return@forEach

                    // Load peer info
                    db.collection("users")
                        .document(peerId)
                        .get()
                        .addOnSuccessListener { userDoc ->
                            val peer = User(
                                uid = peerId,
                                firstName = userDoc.getString("firstName") ?: "",
                                lastName = userDoc.getString("lastName") ?: "",
                                avatarUrl = userDoc.getString("avatarUrl"),
                                isOnline = userDoc.getBoolean("isOnline") ?: false
                            )

                            // Get last message
                            db.collection("conversations")
                                .document(doc.id)
                                .collection("messages")
                                .orderBy("timestamp", com.google.firebase.firestore.Query.Direction.DESCENDING)
                                .limit(1)
                                .get()
                                .addOnSuccessListener { msgSnap ->
                                    val lastMsg = msgSnap.documents.firstOrNull()
                                    val preview = ConversationPreview(
                                        id = doc.id,
                                        peer = peer,
                                        lastMessage = lastMsg?.getString("text") ?: "",
                                        lastMessageTimestamp = lastMsg?.getLong("timestamp") ?: System.currentTimeMillis(),
                                        timeAgo = formatTimeAgo(lastMsg?.getLong("timestamp") ?: System.currentTimeMillis()),
                                        unreadCount = doc.getLong("unreadCount")?.toInt() ?: 0
                                    )
                                    conversations.add(preview)

                                    loadedConversations++
                                    if (loadedConversations == totalConversations) {
                                        originalMessages = conversations.sortedByDescending { it.lastMessageTimestamp }
                                        _uiState.value = _uiState.value.copy(
                                            isLoading = false,
                                            messages = originalMessages
                                        )
                                    }
                                }
                        }
                }
            }
            .addOnFailureListener { e ->
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    error = e.message
                )
            }
    }

    private fun formatTimeAgo(timestamp: Long): String {
        if (timestamp == 0L) return ""
        val now = System.currentTimeMillis()
        val diff = now - timestamp
        return when {
            diff < 60_000 -> "Just now"
            diff < 3600_000 -> "${diff / 60_000}m"
            diff < 86400_000 -> "${diff / 3600_000}h"
            else -> "${diff / 86400_000}d"
        }
    }
}

package com.example.dating.viewmodel
import androidx.lifecycle.ViewModel import androidx.lifecycle.viewModelScope import com.example.dating.data.model.MessagesFilterState import com.example.dating.data.model.ConversationPreview import com.example.dating.data.model.User import com.example.dating.data.model.repository.FirebaseMessagesRepository import com.google.firebase.auth.FirebaseAuth import com.google.firebase.firestore.FirebaseFirestore import dagger.hilt.android.lifecycle.HiltViewModel import kotlinx.coroutines.delay import kotlinx.coroutines.flow.MutableStateFlow import kotlinx.coroutines.flow.StateFlow import kotlinx.coroutines.launch import javax.inject.Inject import com.example.dating.data.model.filterMessages
data class MessagesUiState( val isLoading: Boolean = false, val error: String? = null, val messages: List<ConversationPreview> = emptyList() )
@HiltViewModel class MessagesViewModel @Inject constructor( private val db: FirebaseFirestore, private val auth: FirebaseAuth ) : ViewModel() {
private val _uiState = MutableStateFlow(MessagesUiState()) val uiState: StateFlow<MessagesUiState> = _uiState
private val _filterState = MutableStateFlow(MessagesFilterState()) val filterState: StateFlow<MessagesFilterState> = _filterState // Change to StateFlow
private var originalMessages = listOf<ConversationPreview>() private var loadedConversations = 0 private var totalConversations = 0
init { loadMessages() }
fun updateFilter(newState: MessagesFilterState) { _filterState.value = newState }
fun clearFilter() { _filterState.value = MessagesFilterState() _uiState.value = _uiState.value.copy(messages = originalMessages) }
fun applyFilter() { val filtered = filterMessages(originalMessages, filterState.value) _uiState.value = _uiState.value.copy(messages = filtered) }
private fun loadMessages() { _uiState.value = MessagesUiState(isLoading = true) val uid = auth.currentUser?.uid ?: return android.util.Log.d("MessagesViewModel", "Loading messages for uid: $uid") db.collection("conversations") .whereArrayContains("participants", uid) .get() .addOnSuccessListener { snapshot -> android.util.Log.d("MessagesViewModel", "Conversations snapshot size: ${snapshot.size()}") if (snapshot.isEmpty) { _uiState.value = _uiState.value.copy( isLoading = false, messages = emptyList() ) android.util.Log.d("MessagesViewModel", "No conversations found.") return@addOnSuccessListener }
totalConversations = snapshot.size() loadedConversations = 0 val conversations = mutableListOf<ConversationPreview>()
snapshot.documents.forEach { doc -> val participants = doc.get("participants") as? List<String> ?: return@forEach val peerId = participants.find { it != uid } ?: return@forEach android.util.Log.d("MessagesViewModel", "Conversation doc: ${doc.id}, participants: $participants, peerId: $peerId")
// Load peer info db.collection("users") .document(peerId) .get() .addOnSuccessListener { userDoc -> val peer = User( uid = peerId, firstName = userDoc.getString("firstName") ?: "", lastName = userDoc.getString("lastName") ?: "", avatarUrl = userDoc.getString("avatarUrl"), isOnline = userDoc.getBoolean("isOnline") ?: false ) android.util.Log.d("MessagesViewModel", "Loaded peer: $peer")
// Get last message db.collection("conversations") .document(doc.id) .collection("messages") .orderBy("timestamp", com.google.firebase.firestore.Query.Direction.DESCENDING) .limit(1) .get() .addOnSuccessListener { msgSnap -> val lastMsg = msgSnap.documents.firstOrNull() android.util.Log.d("MessagesViewModel", "Last message: ${lastMsg?.getString("text")}, timestamp: ${lastMsg?.getLong("timestamp")}") val preview = ConversationPreview( id = doc.id, peer = peer, lastMessage = lastMsg?.getString("text") ?: "", lastMessageTimestamp = lastMsg?.getLong("timestamp") ?: System.currentTimeMillis(), timeAgo = formatTimeAgo(lastMsg?.getLong("timestamp") ?: System.currentTimeMillis()), unreadCount = doc.getLong("unreadCount")?.toInt() ?: 0 ) conversations.add(preview)
loadedConversations++ android.util.Log.d("MessagesViewModel", "Loaded $loadedConversations/$totalConversations conversations.") if (loadedConversations == totalConversations) { originalMessages = conversations.sortedByDescending { it.lastMessageTimestamp } android.util.Log.d("MessagesViewModel", "Final loaded messages: $originalMessages") _uiState.value = _uiState.value.copy( isLoading = false, messages = originalMessages ) } } } } } .addOnFailureListener { e -> android.util.Log.e("MessagesViewModel", "Failed to load messages: ${e.message}") _uiState.value = _uiState.value.copy( isLoading = false, error = e.message ) } }
private fun formatTimeAgo(timestamp: Long): String { if (timestamp == 0L) return "" val now = System.currentTimeMillis() val diff = now - timestamp return when { diff < 60_000 -> "Just now" diff < 3600_000 -> "${diff / 60_000}m" diff < 86400_000 -> "${diff / 3600_000}h" else -> "${diff / 86400_000}d" } } }

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.AuthRepository
import com.example.dating.data.model.repository.UserRepository
import com.example.dating.data.model.Resource

@HiltViewModel
class ProfileViewModel @Inject constructor(
    private val userRepository: UserRepository,
    private val auth: AuthRepository,
    ) : ViewModel() {
    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _updateState = MutableStateFlow<Resource<Unit>?>(null)
    val updateState: StateFlow<Resource<Unit>?> = _updateState

    init {
        val uid = auth.currentUser?.uid
        if (uid != null) {
            viewModelScope.launch {
                userRepository.getUser(uid).collect { fetchedUser ->
                    android.util.Log.d("ProfileViewModel", "Fetched user: $fetchedUser")
                    _user.value = fetchedUser
                }
            }
        } else {
            android.util.Log.w("ProfileViewModel", "No UID found in AuthRepository")
        }
    }

    fun loadUser(uid: String) {
        viewModelScope.launch {
            userRepository.getUser(uid).collect { _user.value = it }
        }
    }

    fun updateProfile(user: User) {
        android.util.Log.d("ProfileViewModel", "updateProfile called with: $user")
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
            }
        }
    }

    fun updateGender(gender: String) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(gender = gender)
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateGender(updatedUser.uid, gender)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateInterests(interests: List<String>) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(interests = interests)
        android.util.Log.w("ProfileViewModel", currentUser.toString())

        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateInterests(updatedUser.uid, interests)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateJobLocationDescription(job: String?, location: String?, description: String?) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(job = job, location = location, description = description)
        viewModelScope.launch {
            userRepository.updateJobLocationDescription(updatedUser.uid, job, location, description)
            _user.value = updatedUser
        }
    }

    fun createUser(user: User) {
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.createUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
            }
        }
    }

    fun uploadAvatar(uri: android.net.Uri) {
        val uid = auth.currentUser?.uid ?: return
        val storage = com.google.firebase.storage.FirebaseStorage.getInstance()
        val avatarRef = storage.reference.child("avatars/$uid.jpg")
        viewModelScope.launch {
            try {
                avatarRef.putFile(uri).addOnSuccessListener {
                    avatarRef.downloadUrl.addOnSuccessListener { downloadUrl ->
                        viewModelScope.launch {
                            userRepository.updateAvatarUrl(uid, downloadUrl.toString())
                            android.util.Log.d("ProfileViewModel", "Avatar updated: $downloadUrl")
                        }
                    }
                }.addOnFailureListener { e ->
                    android.util.Log.e("ProfileViewModel", "Failed to upload avatar: ${e.message}")
                }
            } catch (e: Exception) {
                android.util.Log.e("ProfileViewModel", "Exception in uploadAvatar: ${e.message}")
            }
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import dagger.hilt.android.lifecycle.HiltViewModel
