    suspend fun updateJobLocationDescription(uid: String, job: String?, location: String?, description: String?): Resource<Unit> {
        return try {
            firestore.collection("users").document(uid).update(
                mapOf(
                    "job" to job,
                    "location" to location,
                    "description" to description
                )
            ).await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    suspend fun uploadAvatar(uid: String, uri: android.net.Uri): String {
        // TODO: Implement Firebase Storage upload and return the URL
        return ""
    }

    suspend fun createUser(user: User): Resource<Unit> {
        return try {
            firestore.collection("users").document(user.uid).set(user).await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    suspend fun updateAvatarUrl(uid: String, avatarUrl: String) {
        firestore.collection("users").document(uid)
            .update("avatarUrl", avatarUrl)
            .await()
    }

    suspend fun updateFilterPreferences(uid: String, prefs: UserFilterPreferences): Resource<Unit> {
        return try {
            android.util.Log.d("UserRepository", "updateFilterPreferences: uid=$uid, prefs=$prefs")
            firestore.collection("users").document(uid).update("filterPreferences", prefs).await()
            android.util.Log.d("UserRepository", "updateFilterPreferences: success for uid=$uid")
            Resource.Success(Unit)
        } catch (e: Exception) {
            android.util.Log.e("UserRepository", "updateFilterPreferences: failure for uid=$uid", e)
            Resource.Failure(e)
        }
    }
}

package com.example.dating.data.model.repository

import com.example.dating.data.model.Resource
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.tasks.await
import javax.inject.Inject
import com.example.dating.data.model.User

class UserRepositoryImpl @Inject constructor(
    private val firestore: FirebaseFirestore
) : UserRepository {

    fun getUser(uid: String): Flow<User?> = callbackFlow {
        val docRef = firestore.collection("users").document(uid)
        val listener = docRef.addSnapshotListener { snapshot, _ ->
            offer(snapshot?.toObject(User::class.java))
        }
        awaitClose { listener.remove() }
    }

    override suspend fun updateUser(user: User) {
        firestore.collection("users").document(user.uid).set(user).await()
    }

    override suspend fun saveProfile(
        userId: String,
        firstName: String,
        lastName: String,
        birthday: String?,
        imageUrl: String?
    ): Resource<Unit> {
        return try {
            val userMap = hashMapOf(
                "firstName" to firstName,
                "lastName" to lastName,
                "birthday" to birthday,
                "imageUrl" to imageUrl
            )
            firestore.collection("users").document(userId)
                .set(userMap)
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    override suspend fun updateGender(userId: String, gender: String): Resource<Unit> {
        return try {
            firestore.collection("users").document(userId)
                .update("gender", gender)
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    override suspend fun updateInterests(userId: String, interests: List<String>): Resource<Unit> {
        return try {
            firestore.collection("users").document(userId)
                .update("interests", interests)
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }

    override suspend fun updateJobLocationDescription(
        userId: String,
        job: String?,
        location: String?,
        description: String?
    ): Resource<Unit> {
        return try {
            firestore.collection("users").document(userId)
                .update(mapOf(
                    "job" to job,
                    "location" to location,
                    "description" to description
                ))
                .await()
            Resource.Success(Unit)
        } catch (e: Exception) {
            Resource.Failure(e)
        }
    }
}

