        }
        if (likeProgress > 0.05f) {
            Icon(
                imageVector = Icons.Default.Favorite,
                contentDescription = "Like",
                tint = Color.Red.copy(alpha = iconAlpha),
                modifier = Modifier
                    .size((96f * iconScale).dp)
                    .align(Alignment.Center)
            )
        } else if (dislikeProgress > 0.05f) {
            Icon(
                imageVector = Icons.Default.Close,
                contentDescription = "Dislike",
                tint = Color.White.copy(alpha = iconAlpha),
                modifier = Modifier
                    .size((96f * iconScale).dp)
                    .align(Alignment.Center)
            )
        }
    }
}

@Composable
fun ActionButtons(
    profiles: List<User>,
    profileIndex: MutableState<Int>,
    handleProfileAction: suspend (Boolean, MutableState<Int>, List<User>, HomeViewModel) -> Unit,
    animateSwipe: suspend (Animatable<Float, *>, Float) -> Unit,
    homeViewModel: HomeViewModel = viewModel()
) {
    val scope = rememberCoroutineScope()
    val offsetX = remember { Animatable(0f) }

    Row(
        modifier = Modifier
            .fillMaxWidth()
            .padding(vertical = 24.dp),
        horizontalArrangement = Arrangement.spacedBy(25.dp, Alignment.CenterHorizontally),
        verticalAlignment = Alignment.CenterVertically
    ) {
        // Dislike Button
        ActionButton(
            icon = Icons.Default.Close,
            background = Color.White,
            iconTint = Color.Black,
            size = 75.dp,
            shadow = 8.dp
        ) {
            scope.launch {
                handleProfileAction(false, profileIndex, profiles, homeViewModel)
                animateSwipe(offsetX, -1f) // Pass direction explicitly
            }
        }
        // Super Like Button
        ActionButton(
            icon = Icons.Default.Favorite,
            background = AppColors.Text_Pink,
            iconTint = Color.White,
            size = 95.dp,
            shadow = 12.dp
        ) {
            scope.launch {
                handleProfileAction(true, profileIndex, profiles, homeViewModel)
                animateSwipe(offsetX, 1f) // Pass direction explicitly
            }
        }
        // Like Button (calls a different method for clarity)
        ActionButton(
            icon = Icons.Default.Star,
            background = Color(0xFF4A154B),
            iconTint = Color.White,
            size = 75.dp,
            shadow = 8.dp
        ) {
            scope.launch {
                handleProfileAction(true, profileIndex, profiles, homeViewModel)
                animateSwipe(offsetX, 1f) // Pass direction explicitly
            }
        }
    }
}

@Composable
fun ActionButton(
    icon: ImageVector,
    background: Color,
    iconTint: Color,
    size: Dp,
    shadow: Dp,
    onClick: () -> Unit
) {
    Box(
        modifier = Modifier
            .size(size)
            .shadow(shadow, CircleShape)
            .clip(CircleShape)
            .background(background)
            .clickable { onClick() },
        contentAlignment = Alignment.Center
    ) {
        Icon(
            imageVector = icon,
            contentDescription = null,
            tint = iconTint,
            modifier = Modifier.size(size * 0.5f)
        )
    }
}

package com.example.dating.ui.mainscreens

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.google.firebase.Timestamp
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch

class HomeViewModel : ViewModel() {
    private val db = FirebaseFirestore.getInstance()
    private val _addFavoriteState = MutableStateFlow<Result<Unit>?>(null)
    val addFavoriteState: StateFlow<Result<Unit>?> = _addFavoriteState

    fun addFavorite(likedId: String) {
        val likerId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        val favorite = hashMapOf(
            "likerId" to likerId,
            "likedId" to likedId,
            "timestamp" to Timestamp.now()
        )
        viewModelScope.launch {
            try {
                db.collection("favorites").add(favorite)
                _addFavoriteState.value = Result.success(Unit)
            } catch (e: Exception) {
                _addFavoriteState.value = Result.failure(e)
            }
        }
    }

    fun clearAddFavoriteState() {
        _addFavoriteState.value = null
    }
}

package com.example.dating.viewmodel

import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.FavoriteRepository
import com.example.dating.data.model.repository.MatchRepository
import com.example.dating.data.model.repository.HomeRepository
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import kotlinx.coroutines.tasks.await
import com.example.dating.data.model.User
import com.example.dating.data.model.Resource

@HiltViewModel
class HomeViewModel @Inject constructor(
    private val favoriteRepository: FavoriteRepository,
    private val homeRepository: HomeRepository
) : ViewModel() {
    private val db = FirebaseFirestore.getInstance()

    private val _matchFoundUserId = MutableStateFlow<String?>(null)
    val matchFoundUserId: StateFlow<String?> = _matchFoundUserId

    private val _usersState = MutableStateFlow<Resource<List<User>>>(Resource.Loading)
    val usersState: StateFlow<Resource<List<User>>> = _usersState

    init {
        fetchHome()
    }

    private fun fetchHome() {
        val currentUserId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        viewModelScope.launch {
            try {
                _usersState.value = Resource.Loading
                // Get list of user IDs for home (excluding current user)
                val profileIds = homeRepository.fetchProfiles().filter { it != currentUserId }
                if (profileIds.isEmpty()) {
                    _usersState.value = Resource.Success(emptyList())
                    return@launch
                }
                // Fetch user profiles
                val users = homeRepository.getUserProfilesByIds(profileIds)
                _usersState.value = Resource.Success(users)
            } catch (e: Exception) {
                _usersState.value = Resource.Failure(e)
            }
        }
    }

    fun likeProfile(likedId: String) {
        val likerId = FirebaseAuth.getInstance().currentUser?.uid ?: return
        viewModelScope.launch {
            try {
                // Add favorite using repository
                favoriteRepository.addFavorite(likerId, likedId)
                // Check for match using repository
                val isMatch = favoriteRepository.isMatch(likerId, likedId)
                if (isMatch) {
                    _matchFoundUserId.value = likedId
                    android.util.Log.d("HomeViewModel", "Calling MatchRepository.saveMatch with $likerId and $likedId")
                } else {
                    _matchFoundUserId.value = null
                    android.util.Log.d("HomeViewModel", "Calling MatchRepository.saveMatch with $likerId and $likedId, status=false")
                }
            } catch (e: Exception) {
                _usersState.value = Resource.Failure(e as? Exception ?: Exception(e.message))
            }
        }
    }

    fun resetMatchFoundUserId() {
        _matchFoundUserId.value = null
    }
}

