    val isLoading: StateFlow<Boolean> = _isLoading

    private var registration: ListenerRegistration? = null

    /**
     * Gi t UserProfileScreen. Nu uid=null -> ly current user;
     * nu vn null -> fallback dummy  test UI.
     */
    fun observeUser(uid: String?) {
        registration?.remove()
        _isLoading.value = true

        val targetUid = uid ?: auth.currentUser?.uid
        if (targetUid.isNullOrBlank()) {
            _user.value = createDummyUser()
            _isLoading.value = false
            return
        }

        registration = firestore.collection("users")
            .document(targetUid)
            .addSnapshotListener { snap, err ->
                if (err != null || snap == null || !snap.exists()) {
                    _user.value = createDummyUser()
                    _isLoading.value = false
                    return@addSnapshotListener
                }
                _user.value = snapshotToUser(snap, targetUid)
                _isLoading.value = false
            }
    }

    fun updateFilterPreferences(uid: String, prefs: UserFilterPreferences, onResult: (Resource<Unit>) -> Unit) {
        android.util.Log.d("UserViewModel", "updateFilterPreferences called: uid=$uid, prefs=$prefs")
