            val result = userRepository.updateFilterPreferences(uid, prefs)
            android.util.Log.d("UserViewModel", "updateFilterPreferences result: $result for uid=$uid")
            onResult(result)
        }
    }

    fun getCurrentUserId(): String? {
        return FirebaseAuth.getInstance().currentUser?.uid
    }

    override fun onCleared() {
        super.onCleared()
        registration?.remove()
    }

    // ---- Helper: c an ton, lc null ----
    private fun snapshotToUser(doc: DocumentSnapshot, uid: String): User {
        val imageUrl = (doc.get("imageUrl") as? List<*>)?.filterIsInstance<String>() ?: emptyList()
        val interests = (doc.get("interests") as? List<*>)?.filterIsInstance<String>() ?: emptyList()

        return User(
            uid = uid,
            firstName = doc.getString("firstName").orEmpty(),
            lastName  = doc.getString("lastName").orEmpty(),
            birthday  = doc.getString("birthday"),
            imageUrl  = imageUrl,
            avatarUrl = doc.getString("avatarUrl") ?: imageUrl.firstOrNull(),
            gender    = doc.getString("gender"),
            job       = doc.getString("job") ?: "Professional model",
            location  = doc.getString("location") ?: "Chicago, IL, United States",
            description = doc.getString("description"),
            interests   = interests,
            filterPreferences = doc.get("filterPreferences")?.let {
                // Firestore returns a Map, so we need to convert it to UserFilterPreferences
                val prefsMap = it as? Map<*, *>
                UserFilterPreferences(
                    preferredGender = prefsMap?.get("preferredGender") as? String,
                    minAge = (prefsMap?.get("minAge") as? Long)?.toInt(),
                    maxAge = (prefsMap?.get("maxAge") as? Long)?.toInt(),
                    maxDistance = (prefsMap?.get("maxDistance") as? Long)?.toInt()
                )
            }
        )
    }

