@Composable
fun MatchScreen(navController: NavController, matchedUserId: String?) {
    val matchViewModel: MatchViewModel = hiltViewModel()
    val profileViewModel: ProfileViewModel = hiltViewModel()

    val currentUser by profileViewModel.user.collectAsState()
    val currentUserInfo by matchViewModel.userInfo.collectAsState()
    val matchedUserInfo by matchViewModel.matchedUserInfo.collectAsState()

    // Set current user info in MatchViewModel
    LaunchedEffect(currentUser) {
        matchViewModel.setUser(currentUser)
    }

    // Set matched user info in MatchViewModel
    LaunchedEffect(matchedUserId) {
        if (matchedUserId != null) {
            val matchedUser = matchViewModel.getUserById(matchedUserId)
            matchViewModel.setMatchedUser(matchedUser)
        }
    }
    // Save the match when both users are available
    LaunchedEffect(currentUserInfo?.uid, matchedUserInfo?.uid) {
        val uid1 = currentUserInfo?.uid
        val uid2 = matchedUserInfo?.uid
        if (uid1 != null && uid2 != null) {
            matchViewModel.saveMatch(uid1, uid2, true)
        }
    }

    Column(modifier = Modifier.fillMaxSize()) {
        // Main content
        Box(
            modifier = Modifier
                .fillMaxSize()
                .background(AppColors.GradientBackground) // Light purple
        ) {
            Column(
                modifier = Modifier
                    .fillMaxSize()
                    .padding(horizontal = 32.dp),
                horizontalAlignment = Alignment.CenterHorizontally,
                verticalArrangement = Arrangement.Center
            ) {
                Box(modifier = Modifier.height(300.dp)) {

                    // Upper right profile photo (current user)
                    Box(
                        modifier = Modifier
                            .size(width = 190.dp, height = 280.dp)
                            .align(Alignment.TopEnd)
                            .offset(x = 55.dp, y = (-30).dp)
                            .rotate(10f)
                            .clip(RoundedCornerShape(24.dp))
                            .background(Color.Black)
                    ) {
                        if (!currentUserInfo?.avatarUrl.isNullOrBlank()) {
                            coil.compose.AsyncImage(
                                model = currentUserInfo?.avatarUrl,
                                contentDescription = "Current User",
                                contentScale = androidx.compose.ui.layout.ContentScale.Crop,
                                modifier = Modifier.fillMaxSize()
                            )
                        } else {
                            Icon(
                                imageVector = Icons.Default.Person,
                                contentDescription = "Current User",
                                tint = Color.Gray.copy(alpha = 0.4f),
                                modifier = Modifier.fillMaxSize()
                            )
                        }
                    }

                    // Lower left profile photo (matched user)
                    Box(
                        modifier = Modifier
                            .size(width = 190.dp, height = 280.dp)
                            .align(Alignment.BottomStart)
                            .offset(x = (-55).dp, y = 90.dp)
                            .rotate(-10f)
                            .clip(RoundedCornerShape(24.dp))
                            .background(Color.Black)
                    ) {
                        if (!matchedUserInfo?.avatarUrl.isNullOrBlank()) {
                            coil.compose.AsyncImage(
                                model = matchedUserInfo?.avatarUrl,
                                contentDescription = "Matched User",
                                contentScale = androidx.compose.ui.layout.ContentScale.Crop,
                                modifier = Modifier.fillMaxSize()
                            )
                        } else {
                            Icon(
                                imageVector = Icons.Default.Person,
                                contentDescription = "Matched User",
                                tint = Color.Gray.copy(alpha = 0.4f),
                                modifier = Modifier.fillMaxSize()
                            )
                        }
                    }

                    // Heart for upper right photo
                    Box(
                        modifier = Modifier
                            .size(60.dp)
                            .align(Alignment.TopEnd)
                            .offset(x = (-75).dp, y = (-50).dp)
                            .rotate(10f)
                            .background(AppColors.Text_Pink, CircleShape),
                        contentAlignment = Alignment.Center
                    ) {
                        Icon(
                            imageVector = Icons.Default.Favorite,
                            contentDescription = "Heart",
                            tint = Color.White,
                            modifier = Modifier.size(22.dp)
                        )
                    }

                    // Heart for lower left photo
                    Box(
                        modifier = Modifier
                            .size(60.dp)
                            .align(Alignment.BottomStart)
                            .offset(x = (-60).dp, y = 120.dp)
                            .rotate(-10f)
                            .background(AppColors.Text_Pink, CircleShape),
                        contentAlignment = Alignment.Center
                    ) {
                        Icon(
                            imageVector = Icons.Default.Favorite,
                            contentDescription = "Heart",
                            tint = Color.White,
                            modifier = Modifier.size(22.dp)
                        )
                    }

                }
                Spacer(modifier = Modifier.height(160.dp))
                Text(
                    text = "It's a match, ${currentUserInfo?.firstName ?: "You"}!",
                    fontWeight = FontWeight.Bold,
                    fontSize = 32.sp,
                    color = Color.Black,
                    modifier = Modifier.padding(bottom = 8.dp)
                )
                // Example: Show matched user name
                Text(
                    text = "Matched with: ${matchedUserInfo?.firstName ?: "Unknown"}",
                    fontSize = 20.sp,
                    color = Color.Black,
                    modifier = Modifier.padding(bottom = 8.dp)
                )
                // You can use currentUserInfo?.avatarUrl and matchedUserInfo?.avatarUrl for images
                Text(
                    text = "Start a conversation now with each other.",
                    fontSize = 16.sp,
                    color = Color.Black,
                    modifier = Modifier.padding(bottom = 60.dp)
                )
                Button(
                    onClick = {
                        // Find or create a conversation between current user and matched user, then navigate to chat
                        val currentUid = currentUserInfo?.uid
                        val matchedUid = matchedUserInfo?.uid
                        if (currentUid != null && matchedUid != null) {

                        }
                    },
                    colors = ButtonDefaults.buttonColors(containerColor = Color(0xFF4A154B)),
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp)
                ) {
                    Text("Say hello", color = Color.White, fontSize = 18.sp, fontWeight = FontWeight.Bold)
                }
                Spacer(modifier = Modifier.height(16.dp))
                OutlinedButton(
                    onClick = { navController.popBackStack("home", inclusive = false) },
                    modifier = Modifier
                        .fillMaxWidth()
                        .height(56.dp),
                    colors = ButtonDefaults.outlinedButtonColors(containerColor = Color.White)
                ) {
                    Text("Keep swiping", color = Color.Black, fontSize = 18.sp, fontWeight = FontWeight.Bold)
                }
            }
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import androidx.navigation.NavController
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.FirebaseMessagesRepository
import com.example.dating.data.model.repository.MatchRepository
import com.example.dating.data.model.repository.UserRepository
import com.example.dating.navigation.Screen
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.first
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class MatchViewModel @Inject constructor(
    private val matchRepository: MatchRepository,
    private val userRepository: UserRepository,
    private val messagesRepository: FirebaseMessagesRepository
) : ViewModel() {

    data class UserInfo(val uid: String, val firstName: String, val avatarUrl: String?)

    private val _userInfo = MutableStateFlow<UserInfo?>(null)
    val userInfo: StateFlow<UserInfo?> = _userInfo

    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _matchedUserInfo = MutableStateFlow<UserInfo?>(null)
    val matchedUserInfo: StateFlow<UserInfo?> = _matchedUserInfo

    fun setUser(user: User?) {
        _user.value = user
        if (user != null) {
            _userInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = if (user.avatarUrl.isNullOrBlank()) null else user.avatarUrl
            )
        } else {
            _userInfo.value = null
        }
    }

    fun setMatchedUser(user: User?) {
        if (user != null) {
            _matchedUserInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = if (user.avatarUrl.isNullOrBlank()) null else user.avatarUrl
            )
        } else {
            _matchedUserInfo.value = null
        }
    }

    fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        viewModelScope.launch {
            matchRepository.saveMatch(userId1, userId2, status)
            if (status) {
                messagesRepository.createConversation(userId1, userId2)
            }
        }
    }

    suspend fun getUserById(id: String): User? {
        return userRepository.getUser(id).first()
    }

    fun navigateToChat(navController: NavController, currentUid: String, matchedUid: String) {
        viewModelScope.launch {
            val conversationId = getConversation(currentUid, matchedUid)
            if (conversationId != null) {
                navController.navigate(
                    Screen.ChatDetail.createRoute(conversationId)
                )
            }
        }
    }

    suspend fun getConversation(currentUid: String, matchedUid: String): String? {
        // Query Firestore for a conversation with both participants
        return messagesRepository.getConversation(currentUid, matchedUid)
    }


}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import androidx.navigation.NavController
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.FirebaseMessagesRepository
import com.example.dating.data.model.repository.MatchRepository
import com.example.dating.data.model.repository.UserRepository
import com.example.dating.navigation.Screen
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.flow.first
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class MatchViewModel @Inject constructor(
    private val matchRepository: MatchRepository,
    private val userRepository: UserRepository,
    private val messagesRepository: FirebaseMessagesRepository
) : ViewModel() {

    data class UserInfo(val uid: String, val firstName: String, val avatarUrl: String?)

    private val _userInfo = MutableStateFlow<UserInfo?>(null)
    val userInfo: StateFlow<UserInfo?> = _userInfo

    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _matchedUserInfo = MutableStateFlow<UserInfo?>(null)
    val matchedUserInfo: StateFlow<UserInfo?> = _matchedUserInfo

    fun setUser(user: User?) {
        _user.value = user
        if (user != null) {
            _userInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = if (user.avatarUrl.isNullOrBlank()) null else user.avatarUrl
            )
        } else {
            _userInfo.value = null
        }
    }

    fun setMatchedUser(user: User?) {
        if (user != null) {
            _matchedUserInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = if (user.avatarUrl.isNullOrBlank()) null else user.avatarUrl
            )
        } else {
            _matchedUserInfo.value = null
        }
    }

    fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        viewModelScope.launch {
            matchRepository.saveMatch(userId1, userId2, status)
            if (status) {
                messagesRepository.createConversation(userId1, userId2)
            }
        }
    }

    suspend fun getUserById(id: String): User? {
        return userRepository.getUser(id).first()
    }

    fun navigateToChat(navController: NavController, currentUid: String, matchedUid: String) {
        viewModelScope.launch {
            val conversationId = getConversation(currentUid, matchedUid)
            if (conversationId != null) {
                navController.navigate(
                    Screen.ChatDetail.createRoute(conversationId)
                )
            }
        }
    }

    suspend fun getConversation(currentUid: String, matchedUid: String): String? {
        // Query Firestore for a conversation with both participants
        return messagesRepository.getConversation(currentUid, matchedUid)
    }


}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.Resource
import com.example.dating.data.model.User
import com.example.dating.data.model.UserFilterPreferences
import com.example.dating.data.model.repository.AuthRepository
import com.example.dating.data.model.repository.UserRepository
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.storage.FirebaseStorage
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class ProfileViewModel @Inject constructor(
    private val userRepository: UserRepository,
    private val auth: AuthRepository,
    ) : ViewModel() {
    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _updateState = MutableStateFlow<Resource<Unit>?>(null)
    val updateState: StateFlow<Resource<Unit>?> = _updateState

    init {
        val uid = auth.currentUser?.uid
        if (uid != null) {
            viewModelScope.launch {
                userRepository.getUser(uid).collect { fetchedUser ->
                    android.util.Log.d("ProfileViewModel", "Fetched user: $fetchedUser")
                    _user.value = fetchedUser
                }
            }
        } else {
            android.util.Log.w("ProfileViewModel", "No UID found in AuthRepository")
        }
    }


    fun updateProfile(user: User) {
        android.util.Log.d("ProfileViewModel", "updateProfile called with: $user")
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
                try {
                    com.example.dating.data.model.repository.RecommendationRepository(applicationContext).createEmbedding(user)
                } catch (e: Exception) {
                    android.util.Log.e("ProfileViewModel", "Failed to update embedding: ${e.message}")
                }
            }
        }
    }

    fun updateGender(gender: String) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(gender = gender)
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateGender(updatedUser.uid, gender)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateInterests(interests: List<String>) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(interests = interests)
        android.util.Log.w("ProfileViewModel", currentUser.toString())

        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateInterests(updatedUser.uid, interests)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateJobLocationDescription(job: String?, location: String?, description: String?) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(job = job, location = location, description = description)
        viewModelScope.launch {
            userRepository.updateJobLocationDescription(updatedUser.uid, job, location, description)
            _user.value = updatedUser
        }
    }

    fun createUser(user: User) {
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.createUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
            }
        }
    }

    fun uploadAvatar(uri: android.net.Uri) {
        val uid = auth.currentUser?.uid ?: return
        val storage = FirebaseStorage.getInstance()
        val avatarRef = storage.reference.child("avatars/$uid.jpg")
        avatarRef.putFile(uri)
            .addOnSuccessListener { taskSnapshot: com.google.firebase.storage.UploadTask.TaskSnapshot ->
                avatarRef.downloadUrl.addOnSuccessListener { downloadUrl: android.net.Uri ->
                    viewModelScope.launch {
                        userRepository.updateAvatarUrl(uid, downloadUrl.toString())
                        android.util.Log.d("ProfileViewModel", "Avatar updated: $downloadUrl")
                    }
                }
            }
            .addOnFailureListener { e: Exception ->
                android.util.Log.e("ProfileViewModel", "Failed to upload avatar: ${e.message}")
            }
    }


    fun updateFilterPreferences(uid: String, prefs: UserFilterPreferences, onResult: (Resource<Unit>) -> Unit) {
        android.util.Log.d("UserViewModel", "updateFilterPreferences called: uid=$uid, prefs=$prefs")
        CoroutineScope(Dispatchers.IO).launch {
            val result = userRepository.updateFilterPreferences(uid, prefs)
            android.util.Log.d("UserViewModel", "updateFilterPreferences result: $result for uid=$uid")
            onResult(result)
        }
    }

    fun getCurrentUserId(): String? {
        return FirebaseAuth.getInstance().currentUser?.uid
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.Resource
import com.example.dating.data.model.User
import com.example.dating.data.model.UserFilterPreferences
import com.example.dating.data.model.repository.AuthRepository
import com.example.dating.data.model.repository.UserRepository
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.storage.FirebaseStorage
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.CoroutineScope
import kotlinx.coroutines.Dispatchers
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import javax.inject.Inject

@HiltViewModel
class ProfileViewModel @Inject constructor(
    private val userRepository: UserRepository,
    private val auth: AuthRepository,
    private val recommendationRepository: com.example.dating.data.model.repository.RecommendationRepository // Injected instance
    ) : ViewModel() {
    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _updateState = MutableStateFlow<Resource<Unit>?>(null)
    val updateState: StateFlow<Resource<Unit>?> = _updateState

    init {
        val uid = auth.currentUser?.uid
        if (uid != null) {
            viewModelScope.launch {
                userRepository.getUser(uid).collect { fetchedUser ->
                    android.util.Log.d("ProfileViewModel", "Fetched user: $fetchedUser")
                    _user.value = fetchedUser
                }
            }
        } else {
            android.util.Log.w("ProfileViewModel", "No UID found in AuthRepository")
        }
    }


    fun updateProfile(user: User) {
        android.util.Log.d("ProfileViewModel", "updateProfile called with: $user")
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
                try {
                    // Use injected RecommendationRepository instead of creating a new instance
                    recommendationRepository.createEmbedding(user)
                } catch (e: Exception) {
                    android.util.Log.e("ProfileViewModel", "Failed to update embedding: ${e.message}")
                }
            }
        }
    }

    fun updateGender(gender: String) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(gender = gender)
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateGender(updatedUser.uid, gender)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateInterests(interests: List<String>) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(interests = interests)
        android.util.Log.w("ProfileViewModel", currentUser.toString())

        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.updateInterests(updatedUser.uid, interests)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = updatedUser
            }
        }
    }

    fun updateJobLocationDescription(job: String?, location: String?, description: String?) {
        val currentUser = _user.value ?: return
        val updatedUser = currentUser.copy(job = job, location = location, description = description)
        viewModelScope.launch {
            userRepository.updateJobLocationDescription(updatedUser.uid, job, location, description)
            _user.value = updatedUser
        }
    }

    fun createUser(user: User) {
        viewModelScope.launch {
            _updateState.value = Resource.Loading
            val result = userRepository.createUser(user)
            _updateState.value = result
            if (result is Resource.Success) {
                _user.value = user
            }
        }
    }

    fun uploadAvatar(uri: android.net.Uri) {
        val uid = auth.currentUser?.uid ?: return
        val storage = FirebaseStorage.getInstance()
        val avatarRef = storage.reference.child("avatars/$uid.jpg")
        avatarRef.putFile(uri)
            .addOnSuccessListener { taskSnapshot: com.google.firebase.storage.UploadTask.TaskSnapshot ->
                avatarRef.downloadUrl.addOnSuccessListener { downloadUrl: android.net.Uri ->
                    viewModelScope.launch {
                        userRepository.updateAvatarUrl(uid, downloadUrl.toString())
                        android.util.Log.d("ProfileViewModel", "Avatar updated: $downloadUrl")
                    }
                }
            }
            .addOnFailureListener { e: Exception ->
                android.util.Log.e("ProfileViewModel", "Failed to upload avatar: ${e.message}")
            }
    }


    fun updateFilterPreferences(uid: String, prefs: UserFilterPreferences, onResult: (Resource<Unit>) -> Unit) {
        android.util.Log.d("UserViewModel", "updateFilterPreferences called: uid=$uid, prefs=$prefs")
        CoroutineScope(Dispatchers.IO).launch {
            val result = userRepository.updateFilterPreferences(uid, prefs)
            android.util.Log.d("UserViewModel", "updateFilterPreferences result: $result for uid=$uid")
            onResult(result)
        }
    }

    fun getCurrentUserId(): String? {
        return FirebaseAuth.getInstance().currentUser?.uid
    }
}
