import androidx.compose.ui.unit.sp
import androidx.navigation.NavController

@OptIn(ExperimentalMaterial3Api::class)
@Composable
fun FilterScreen(navController: NavController) {
    // State variables
    var selectedInterest by remember { mutableStateOf("Girls") }
    var location by remember { mutableStateOf("Chicago, USA") }
    var distance by remember { mutableFloatStateOf(40f) }
    var ageRange by remember { mutableStateOf(20f..28f) }

    // Bottom sheet or dialog content
    Column(
        modifier = Modifier
            .fillMaxWidth()
            .wrapContentHeight()
            .clip(RoundedCornerShape(topStart = 24.dp, topEnd = 24.dp))
            .background(Color.White)
            .padding(16.dp),
        horizontalAlignment = Alignment.CenterHorizontally
    ) {
        // Notch
        Box(
            modifier = Modifier
                .width(40.dp)
                .height(4.dp)
                .background(Color.Gray.copy(alpha = 0.5f), CircleShape)
                .align(Alignment.CenterHorizontally)
        )

        Spacer(modifier = Modifier.height(16.dp))

        // Title and Clear
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween,
            verticalAlignment = Alignment.CenterVertically
        ) {
            Text(
                text = "Filters",
                fontSize = 20.sp,
                fontWeight = FontWeight.Bold,
                color = Color.Black
            )
            TextButton(onClick = { /* TODO: Clear filters */ }) {
                Text(
                    text = "Clear",
                    color = Color(0xFFFF69B4), // Pink
                    fontSize = 16.sp
                )
            }
        }

        Spacer(modifier = Modifier.height(24.dp))

        // Interested in
        Text(
            text = "Interested in",
            fontSize = 16.sp,
            color = Color.Gray,
            modifier = Modifier.align(Alignment.Start)
        )
        Spacer(modifier = Modifier.height(8.dp))
        SegmentedButtonRow(modifier = Modifier.fillMaxWidth()) {
            SegmentedButton(
                selected = selectedInterest == "Girls",
                onClick = { selectedInterest = "Girls" },
                shape = RoundedCornerShape(topStart = 8.dp, bottomStart = 8.dp)
            ) {
                Text("Girls")
            }
            SegmentedButton(
                selected = selectedInterest == "Boys",
                onClick = { selectedInterest = "Boys" },
                shape = RoundedCornerShape(0.dp)
            ) {
                Text("Boys")
            }
            SegmentedButton(
                selected = selectedInterest == "Both",
                onClick = { selectedInterest = "Both" },
                shape = RoundedCornerShape(topEnd = 8.dp, bottomEnd = 8.dp)
            ) {
                Text("Both")
            }
        }

        Spacer(modifier = Modifier.height(24.dp))

        // Location
        Text(
            text = "Location",
            fontSize = 16.sp,
            color = Color.Gray,
            modifier = Modifier.align(Alignment.Start)
        )
        Spacer(modifier = Modifier.height(8.dp))
        OutlinedTextField(
            value = location,
            onValueChange = { /* Read-only, tap to select */ },
            readOnly = true,
            modifier = Modifier.fillMaxWidth(),
            trailingIcon = {
                Icon(
                    imageVector = Icons.Default.ArrowForward,
                    contentDescription = "Select location",
                    tint = Color(0xFFFF0000) // Red
                )
            },
            singleLine = true,
            interactionSource = remember { MutableInteractionSource() }.also { interactionSource ->
                // TODO: Handle click to open location selector
            }
        )

        Spacer(modifier = Modifier.height(24.dp))

        // Distance
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            Text(
                text = "Distance",
                fontSize = 16.sp,
                color = Color.Gray
            )
            Text(
                text = "${distance.toInt()}km",
                fontSize = 16.sp,
                color = Color.Gray
            )
        }
        Slider(
            value = distance,
            onValueChange = { distance = it },
            valueRange = 0f..100f, // Assuming max 100km
            modifier = Modifier.fillMaxWidth(),
            colors = SliderDefaults.colors(
                thumbColor = Color(0xFFBB86FC), // Purple
                activeTrackColor = Color(0xFFBB86FC),
                inactiveTrackColor = Color.LightGray
            )
        )

        Spacer(modifier = Modifier.height(24.dp))

        // Age
        Row(
            modifier = Modifier.fillMaxWidth(),
            horizontalArrangement = Arrangement.SpaceBetween
        ) {
            Text(
                text = "Age",
                fontSize = 16.sp,
                color = Color.Gray
            )
            Text(
                text = "${ageRange.start.toInt()}-${ageRange.endInclusive.toInt()}",
                fontSize = 16.sp,
                color = Color.Gray
            )
        }
        RangeSlider(
            value = ageRange,
            onValueChange = { ageRange = it },
            valueRange = 18f..100f, // Assuming age range
            modifier = Modifier.fillMaxWidth(),
            colors = SliderDefaults.colors(
                thumbColor = Color(0xFFBB86FC), // Purple
                activeTrackColor = Color(0xFFBB86FC),
                inactiveTrackColor = Color.LightGray
            )
        )

        Spacer(modifier = Modifier.height(32.dp))

        // Continue button
        Button(
            onClick = { /* TODO: Apply filters and navigate */ },
            modifier = Modifier
                .fillMaxWidth()
                .height(48.dp),
            colors = ButtonDefaults.buttonColors(containerColor = Color(0xFFBB86FC).copy(alpha = 0.2f)), // Light purple
            shape = RoundedCornerShape(24.dp)
        ) {
            Text(
                text = "Continue",
                color = Color.Black,
                fontSize = 16.sp,
                fontWeight = FontWeight.Bold
            )
        }
    }
}

package com.example.dating.ui.components

import androidx.compose.foundation.background
import androidx.compose.foundation.layout.*
import androidx.compose.foundation.shape.CircleShape
import androidx.compose.foundation.shape.RoundedCornerShape
import androidx.compose.material.icons.Icons
import androidx.compose.material.icons.filled.ArrowForward
import androidx.compose.material3.*
import androidx.compose.runtime.*
import androidx.compose.ui.Alignment
import androidx.compose.ui.Modifier
import androidx.compose.ui.draw.clip
import androidx.compose.ui.graphics.Color
