                        about,
                        style = MaterialTheme.typography.bodyMedium,
                        maxLines = if (expanded) Int.MAX_VALUE else 3,
                        overflow = TextOverflow.Ellipsis
                    )
                    if (!expanded) {
                        Text(
                            buildAnnotatedString {
                                withStyle(SpanStyle(color = MaterialTheme.colorScheme.primary, fontWeight = FontWeight.SemiBold)) {
                                    append("Read more")
                                }
                            },
                            modifier = Modifier.padding(top = 6.dp).clickable { expanded = true }
                        )
                    }

                    // Interests
                    if (user.interests.isNotEmpty()) {
                        Spacer(Modifier.height(16.dp))
                        Text("Interests", style = MaterialTheme.typography.titleMedium, fontWeight = FontWeight.SemiBold)
                        Spacer(Modifier.height(8.dp))
                        FlowRow(
                            modifier = Modifier.fillMaxWidth(),
                            horizontalArrangement = Arrangement.spacedBy(8.dp),
                            verticalArrangement = Arrangement.spacedBy(8.dp)
                        ) {
                            user.interests.forEachIndexed { i, itx ->
                                if (i < 2) FilterChip(selected = true, onClick = {}, label = { Text(itx) })
                                else AssistChip(onClick = {}, label = { Text(itx) })
                            }
                        }
                    }

                    // Gallery
                    Spacer(Modifier.height(16.dp))
                    Row(verticalAlignment = Alignment.CenterVertically, modifier = Modifier.fillMaxWidth()) {
                        Text("Gallery", style = MaterialTheme.typography.titleMedium, fontWeight = FontWeight.SemiBold, modifier = Modifier.weight(1f))
                        Text(
                            "See all",
                            color = MaterialTheme.colorScheme.primary,
                            style = MaterialTheme.typography.labelLarge,
                            modifier = Modifier.clickable {
                                if (images.isNotEmpty()) onSeeAll(images)
                            }
                        )
                    }
                    Spacer(Modifier.height(8.dp))

                    if (images.isNotEmpty()) {
                        val preview = images.take(6)
                        LazyVerticalGrid(
                            columns = GridCells.Fixed(3),
                            horizontalArrangement = Arrangement.spacedBy(8.dp),
                            verticalArrangement = Arrangement.spacedBy(8.dp),
                            modifier = Modifier
                                .fillMaxWidth()
                                .height(((preview.size + 2) / 3 * 110).dp)
                        ) {
                            items(preview) { img ->
                                val idx = images.indexOf(img)
                                AsyncImage(
                                    model = img,
                                    contentDescription = null,
                                    placeholder = painterResource(R.drawable.ic_avatar),
                                    error = painterResource(R.drawable.ic_avatar),
                                    contentScale = ContentScale.Crop,
                                    modifier = Modifier
                                        .aspectRatio(1f)
                                        .clip(RoundedCornerShape(12.dp))
                                        .clickable { onImageClick(idx, images) }
                                )
                            }
                        }
                    } else {
                        // Empty state gallery
                        Surface(
                            tonalElevation = 1.dp,
                            shape = RoundedCornerShape(12.dp),
                            modifier = Modifier
                                .fillMaxWidth()
                                .height(120.dp)
                        ) {
                            Box(Modifier.fillMaxSize(), contentAlignment = Alignment.Center) {
                                Text("No images available", color = MaterialTheme.colorScheme.onSurfaceVariant)
                            }
                        }
                    }
                }
            }
        }

        item { Spacer(Modifier.height(28.dp)) }
    }
}

@Composable
private fun CircleButton(icon: @Composable () -> Unit, container: Color, content: Color) {
    Surface(
        color = container,
        contentColor = content,
        shape = CircleShape,
        shadowElevation = 8.dp,
        modifier = Modifier.size(56.dp)
    ) { Box(contentAlignment = Alignment.Center) { icon() } }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.MatchRepository
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.UserRepository
import kotlinx.coroutines.flow.first
import com.example.dating.data.model.repository.FirebaseMessagesRepository

@HiltViewModel
class MatchViewModel @Inject constructor(
    private val matchRepository: MatchRepository,
    private val userRepository: UserRepository,
    private val messagesRepository: FirebaseMessagesRepository
) : ViewModel() {

    data class UserInfo(val uid: String, val firstName: String, val avatarUrl: String?)

    private val _userInfo = MutableStateFlow<UserInfo?>(null)
    val userInfo: StateFlow<UserInfo?> = _userInfo

    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _matchedUserInfo = MutableStateFlow<UserInfo?>(null)
    val matchedUserInfo: StateFlow<UserInfo?> = _matchedUserInfo

    fun setUser(user: User?) {
        _user.value = user
        if (user != null) {
            _userInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = user.avatarUrl
            )
        } else {
            _userInfo.value = null
        }
    }

    fun setMatchedUser(user: User?) {
        if (user != null) {
            _matchedUserInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = user.avatarUrl
            )
        } else {
            _matchedUserInfo.value = null
        }
    }

    fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        viewModelScope.launch {
            matchRepository.saveMatch(userId1, userId2, status)
            if (status) {
                messagesRepository.createConversation(userId1, userId2)
            }
        }
    }

    suspend fun getUserById(id: String): User? {
        return userRepository.getUser(id).first()
    }


}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.repository.MatchRepository
import dagger.hilt.android.lifecycle.HiltViewModel
import javax.inject.Inject
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.UserRepository
import kotlinx.coroutines.flow.first
import com.example.dating.data.model.repository.FirebaseMessagesRepository

@HiltViewModel
class MatchViewModel @Inject constructor(
    private val matchRepository: MatchRepository,
    private val userRepository: UserRepository,
    private val messagesRepository: FirebaseMessagesRepository
) : ViewModel() {

    data class UserInfo(val uid: String, val firstName: String, val avatarUrl: String?)

    private val _userInfo = MutableStateFlow<UserInfo?>(null)
    val userInfo: StateFlow<UserInfo?> = _userInfo

    private val _user = MutableStateFlow<User?>(null)
    val user: StateFlow<User?> = _user

    private val _matchedUserInfo = MutableStateFlow<UserInfo?>(null)
    val matchedUserInfo: StateFlow<UserInfo?> = _matchedUserInfo

    fun setUser(user: User?) {
        _user.value = user
        if (user != null) {
            _userInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = if (user.avatarUrl.isNullOrBlank()) null else user.avatarUrl
            )
        } else {
            _userInfo.value = null
        }
    }

    fun setMatchedUser(user: User?) {
        if (user != null) {
            _matchedUserInfo.value = UserInfo(
                uid = user.uid,
                firstName = user.firstName,
                avatarUrl = if (user.avatarUrl.isNullOrBlank()) null else user.avatarUrl
            )
        } else {
            _matchedUserInfo.value = null
        }
    }

    fun saveMatch(userId1: String, userId2: String, status: Boolean) {
        viewModelScope.launch {
            matchRepository.saveMatch(userId1, userId2, status)
            if (status) {
                messagesRepository.createConversation(userId1, userId2)
            }
        }
    }

    suspend fun getUserById(id: String): User? {
        return userRepository.getUser(id).first()
    }


}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.MessagesFilterState
import com.example.dating.data.model.ConversationPreview
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.FirebaseMessagesRepository
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.delay
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import javax.inject.Inject
import com.example.dating.data.model.filterMessages

data class MessagesUiState(
    val isLoading: Boolean = false,
    val error: String? = null,
    val messages: List<ConversationPreview> = emptyList()
)

@HiltViewModel
class MessagesViewModel @Inject constructor(
    private val db: FirebaseFirestore,
    private val auth: FirebaseAuth
) : ViewModel() {

    private val _uiState = MutableStateFlow(MessagesUiState())
    val uiState: StateFlow<MessagesUiState> = _uiState

    private val _filterState = MutableStateFlow(MessagesFilterState())
    val filterState: StateFlow<MessagesFilterState> = _filterState // Change to StateFlow

    private var originalMessages = listOf<ConversationPreview>()
    private var loadedConversations = 0
    private var totalConversations = 0

    private val repository = FirebaseMessagesRepository(db, auth)

    init {
        loadMessages()
    }

    fun updateFilter(newState: MessagesFilterState) {
        _filterState.value = newState
    }

    fun clearFilter() {
        _filterState.value = MessagesFilterState()
        _uiState.value = _uiState.value.copy(messages = originalMessages)
    }

    fun applyFilter() {
        val filtered = filterMessages(originalMessages, filterState.value)
        _uiState.value = _uiState.value.copy(messages = filtered)
    }

    private fun loadMessages() {
        _uiState.value = MessagesUiState(isLoading = true)
        viewModelScope.launch {
            repository.getConversations().collect { conversations ->
                originalMessages = conversations.sortedByDescending { it.lastMessageTimestamp }
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    messages = originalMessages
                )
            }
        }
    }

    private fun formatTimeAgo(timestamp: Long): String {
        if (timestamp == 0L) return ""
        val now = System.currentTimeMillis()
        val diff = now - timestamp
        return when {
            diff < 60_000 -> "Just now"
            diff < 3600_000 -> "${diff / 60_000}m"
            diff < 86400_000 -> "${diff / 3600_000}h"
            else -> "${diff / 86400_000}d"
        }
    }
}

package com.example.dating.viewmodel

import androidx.lifecycle.ViewModel
import androidx.lifecycle.viewModelScope
import com.example.dating.data.model.MessagesFilterState
import com.example.dating.data.model.ConversationPreview
import com.example.dating.data.model.User
import com.example.dating.data.model.repository.FirebaseMessagesRepository
import com.google.firebase.auth.FirebaseAuth
import com.google.firebase.firestore.FirebaseFirestore
import dagger.hilt.android.lifecycle.HiltViewModel
import kotlinx.coroutines.delay
import kotlinx.coroutines.flow.MutableStateFlow
import kotlinx.coroutines.flow.StateFlow
import kotlinx.coroutines.launch
import javax.inject.Inject
import com.example.dating.data.model.filterMessages

data class MessagesUiState(
    val isLoading: Boolean = false,
    val error: String? = null,
    val messages: List<ConversationPreview> = emptyList()
)

@HiltViewModel
class MessagesViewModel @Inject constructor(
    private val db: FirebaseFirestore,
    private val auth: FirebaseAuth
) : ViewModel() {

    private val _uiState = MutableStateFlow(MessagesUiState())
    val uiState: StateFlow<MessagesUiState> = _uiState

    private val _filterState = MutableStateFlow(MessagesFilterState())
    val filterState: StateFlow<MessagesFilterState> = _filterState // Change to StateFlow

    private var originalMessages = listOf<ConversationPreview>()
    private var loadedConversations = 0
    private var totalConversations = 0

    private val repository = FirebaseMessagesRepository(db, auth)

    init {
        loadMessages()
    }

    fun updateFilter(newState: MessagesFilterState) {
        _filterState.value = newState
    }

    fun clearFilter() {
        _filterState.value = MessagesFilterState()
        _uiState.value = _uiState.value.copy(messages = originalMessages)
    }

    fun applyFilter() {
        val filtered = filterMessages(originalMessages, filterState.value)
        _uiState.value = _uiState.value.copy(messages = filtered)
    }

    private fun loadMessages() {
        _uiState.value = MessagesUiState(isLoading = true)
        val currentUid = auth.currentUser?.uid ?: ""
        viewModelScope.launch {
            repository.getConversations(currentUid).collect { conversations ->
                originalMessages = conversations.sortedByDescending { it.lastMessageTimestamp }
                _uiState.value = _uiState.value.copy(
                    isLoading = false,
                    messages = originalMessages
                )
            }
        }
    }

    private fun formatTimeAgo(timestamp: Long): String {
        if (timestamp == 0L) return ""
        val now = System.currentTimeMillis()
        val diff = now - timestamp
        return when {
            diff < 60_000 -> "Just now"
            diff < 3600_000 -> "${diff / 60_000}m"
            diff < 86400_000 -> "${diff / 3600_000}h"
            else -> "${diff / 86400_000}d"
        }
    }
}
