                arguments = listOf(navArgument("uid") { type = NavType.StringType })
            ) { backStackEntry ->
                val uid = backStackEntry.arguments?.getString("uid")
                UserProfileScreen(navController = navController, userUid = uid)
            }

            // Photo viewer
            composable(
                route = Screen.PhotoViewer.route,
                arguments = listOf(navArgument("startIndex") { type = NavType.IntType })
            ) { backStackEntry ->
                val startIndex = backStackEntry.arguments?.getInt("startIndex") ?: 0
                val images: ArrayList<String> =
                    navController.previousBackStackEntry
                        ?.savedStateHandle
                        ?.get<ArrayList<String>>("images") ?: arrayListOf()

                PhotoViewerScreen(
                    navController = navController,
                    images = images,
                    startIndex = startIndex
                )
            }

            // Add this composable for viewing stories
            composable(
                route = "story_viewer/{uid}",
                arguments = listOf(navArgument("uid") { type = NavType.StringType })
            ) { backStackEntry ->
                val uid = backStackEntry.arguments?.getString("uid") ?: return@composable
                com.example.dating.ui.story.StoryViewerScreen(uid = uid, navController = navController)
            }
        }
    }
}

